css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

body {
  color: #E2EAC9 !important;
  background-color: #2D2626 !important;
}

pre, #Head {
  color: #E2EAC9 !important;
  background-color: #382E2E !important;
}

#main-header {
  background-color: #382E2E !important;
}
#main-header a, #main-header a:hover {
  color: #4F5E4F !important;
}

#install {
  background-color: #382E2E !important;
  display: inline-block !important;
}


#main-article a {
  color: #7DA57C !important;
  text-decoration: none !important;
}

#left-sidebar > * {
    background-color: #5D6868 !important;
}
#left-sidebar a {
    color: #BFCCCC !important;
}

#show-search-options,
#social,
#Foot
{
    display: none !important;
}

/* viewing code */

#view-code, .MessageForm, .Tabs, .Headings {
  background-color: #2D2626 !important;
}

/* Forums section.. */

#Head #Menu a { color: #7DA57C !important; }

.Mine {
    background-color: #382E2E !important;
}

.Comment .Message
{
  color: #7DA57C !important;
}

.Mine .Message
{
  color: #E2EAC9 !important;
}
.BigButton,
.Button,
.TabLink,
.Box,
.PanelInfo li,
.PanelInfo li a,
#Form_Body,
#Form_Search
{
      background: #382E2E !important;
      text-shadow: none !important;
      color: #E2EAC9 !important;
}

div.Comment a,
#Panel h4 a,
div.Menu a span {
  color: #C8CE9C !important;
}

#Form_Search {
  border: #252928 solid 1px !important;
}

.TabLink,
.Button,
div.Tabs.HeadingTabs.DiscussionTabs,
.Mine,
.MessageForm,
.Tabs,
.Headings,
.PanelInfo li,
.MessageList .Item
{
  border: none !important; 
}

}
