css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {

body, html, div.content
{
  background-color: #255F89 !important;
  color: #85A8BF !important;
}
a { color: #A9C2D3 !important; }
a.thing, a.title, a.loggedin {
color: #A9C2D3 !important;
}

/* sidebar */
div.side, #login_login-main {
  background-color: #255F89 !important;
  border: none !important;
}

#search { display: none !important}

div.titlebox { display: none !important}

div.linkinfo {
  background-color: #255F89 !important;
  border: none !important;
}


/* submit link */
.morelink { 
  background: #255F89 !important;
  border: none !important;
}
/* reddit is for nubs */
div.nub { display: none !important; }

/* sidebar shittyness */
div.sidebox.submit { background-color: #255F89 !important; }
div.sidebox.submit div.spacer { display: none !important; }

/* need to be logged in for these */
div.sidecontentbox { display: none !important; }
div.sidebox.create div.spacer { display: none !important; }


.content { margin: 0px; padding: 7px 5px 0 !important ; }
.spacer { margin: 0px !important; }
.content { background-color: #255F89 !important; }

div.footer-parent { display: none !important; }

p.title a { color: #A9C2D3 !important; }

.thing { background-color: #255F89 !important; }
.link.odd { background-color: #255F89 !important; }
.link.even { background-color: #255F89 !important;}


.tagline a { color: #A9C2D3 !important; }
#sr-header-area { 
  background-color: #255F89 !important;
  border: none !important;
}
.sr-bar a { color: #A9C2D3 !important; }

.dropdown.srdrop .selected { color: #A9C2D3 !important; }

#header { background-color: #194D72 !important; }

#header-bottom-right {
  background-color: #194D72 !important;
  border: none !important;
}
#header-bottom-right a
{
  color: #A9C2D3 !important;
}
.tabmenu a { background-color: #0E4368 !important; }

.tabmenu li.selected a { color: #A9C2D3 !important; border: none !important; }


#siteTable_organic { display: none; }

textarea {
  background: #0E4368 !important;
  /*color: white !important;*/
}

.link.odd .entry { border: none !important; }

div.linklisting div.link { margin: 0px !important; padding: 0px 0px 8px !important; }

div.sitetable.linklisting { border: none !important; padding: 0px !important; }

div.linklisting div.md, .infobar {
  background-color: #0E4368 !important;
  color: #A9B2A3  !important;
  border: none !important;
}

/* clicking context makes some shit yellow... */
.usertext.border .usertext-body {
  background-color: #134889 !important;
}


.linkinfo .upvotes { color: #A7C1D1 !important; }

}
