css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arstechnica.com") {

/** hiding stupid shit */
#sidebar, 
#footer, 
#read-more-stories, 
#links-bar, 
#comments-bar, 
#masthead,
#secondary-navigation,
#auxiliary-navigation
 {
  display: none !important;
}

/* these have absolute width so when we hide sidebar it doesnt expand */
#content, #main { width: 100% !important; }

/* make whole page a little bigger */
#page { width: 70% !important; }


/* color changes */

/* font */
#story .body h2,
#generic-page .body h2,
#story .body h3,
#generic-page .body h3,
h2.title { 
  color: #ECF2CB !important; 
}
#story .body p, #generic-page .body p { 
  color: #F2FCB8 !important; 
}

/* background */
#main {
  background-color: #201525 !important; 
}

/* captions on pictures in the articles */
#story .news-item-figure-caption {
  background-color: #201525 !important; 
}

/* shadow text wtf */
#story .news-item-figure-caption
{
  text-shadow: none !important;
}
#story .news-item-figure-caption .news-item-figure-caption-byline a, #generic-page .news-item-figure-caption .news-item-figure-caption-byline a,
#story .news-item-figure-caption .news-item-figure-caption-text, #generic-page .news-item-figure-caption .news-item-figure-caption-text
 {
  color: white !important;
  font-style: normal !important;
  font-weight: normal !important;
}

}
