css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
    body {
        background: #577543 !important;
        background-image: none !important;
    }

    h1 {
        color: #fff !important;
    }

    h2 {
        color: #eee !important;
    }

    #content_bottom, #content_top {
        width: 0px !important;
    }

    #label_for_channel_id, #add_photo_link a{
        color: #eee !important;
    }

    .clear_tags, .advanced_option {
        color: #eee !important;
    }

    .camera {
        background: #fff !important;
        border-radius: 0px !important;
        opacity: 1 !important;
        padding-bottom: 4px;
        /*box-shadow: 1px 1px 6px rgba(0, 0, 0, 0.6) !important;*/
    }

    .selected {
        background: none !important;
        border-radius: 8px 8px 0px 0px;
    }

    .following {
        color: #eee !important;
        text-shadow: 1px 1px #222 !important;
    }



#posts .post .permalink {
    background: url("http://28.media.tumblr.com/tumblr_lu69x0fiGC1qcayx8o1_100.png") repeat scroll 0 0 transparent !important;
}

#posts .notification, .add_and_remove, .mass_editor, .MissingE_marklist_title {
    border: 0px !important;
    background-color: #577543 !important;
}

.nipple {
    position: fixed !important;
    top: -20px !important;
    left: -20px !important;
}
/*    div.l-header {
        height: 56px !important;
        padding-bottom: 0px !important;
        background: #274862 !important;
        position: fixed !important;
        margin-bottom: -10px !important;
        box-shadow: 0px 15px 10px #274862;
    }*/

    div.l-content, #container {
        background: #477527 !important;
    }

    #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
        padding-top: 10px !important;
    }

    #container {
        box-shadow: 1px 1px 6px rgba(0, 0, 0, 0.6) !important;
        padding-bottom: 20px !important;
    }

    .selection_nipple {
        position: fixed;
        top: -2000px;
        left: -2000px;
    	height: 0 !important;
    	width: 0 !important;
        box-shadow: none !important;
    }

    /* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1121.photobucket.com/albums/l508/famellad/asdf.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}


/*    #content_top, #content_bottom, .inline_external_image, .photo {
        border-radius: 0px !important;
        box-shadow: none !important;
    }*/   
}
