@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chiebukuro.yahoo.co.jp") {
/*フォントサイズ*/
#wrapper{font-size:14px;}

.AttachedInfo-container{font-size:10px;}

#ques,
.details-inner h2 span{font-size:12px!important;}

div .user-name{top:-10px!important;}
body{background:#dcdcd4;}
/*背景*/
.AttachedInfo-container,
.AnswerInformation,
.bestAnswer,
.questioner,
.details-inner,
.Extends-details,
.answers-title,
#wrapper,
.Thanks-comment ul,
.comment-text,
.comment-date{background:none!important;}
.navi-nenu-midwrap{background:none!important;
