height:9px !important;
width:47px !important;
margin-top:1px;
	display: block !important;
	color: #fff !important;
	text-decoration: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
	white-space: nowrap;
}
/*メイン高さ*/
#contents-body{padding-top: 30px!important;}
/*質問*/
.AttachedInfo-container{height:0px;}

#contents-body{left:-100px!important;}
.qa-title,
.Thanks-comment,
#yschorder dl,
#tags ul,
#recommend{
position:absolute;
left:100px!important;}
#recommend{margin-top:180px!important;}

/*タグ*/
#tags ul{margin-top: -18px!important;
padding-top: 0px!important;
padding-bottom: 0px!important;}
/*不要なボタン*/
.q-btn,
#uhdMain,
.yjmth,
#tags > p,
#detailShoulder,
#subNoteLinkWrap,
.Extends-sub-module,
#respondentCounterWrapInn,
#reliability,
#footer,
#tags > h3,
.questioner-violation-report-botton,
.violation-report-botton,
.bm-help,
#img_add_collection,
#yjPofeeButton1,
.useful,
.new-registration,
.randomqa,
.qa-box,
.infoborder-none,
.guest-img,
.avatar,
.details-inner h2,
#im_main1,#im_main2{display:none;}



/*top page*/
.sub-module-container,
.recom-my-module > h2,
.recom-my-inn,
.mobile-service-module,
#cat-inn{display:none;}

/*tags page*/
.tagbaloonInn,
.tagbaloon{background:none!important;}
#sub{display:none;}

/*note page*/
.sqbNote > table > tbody > tr > td,
#sendAdvise,#spread,#detailBkm,
#sendQA{display:none;}
#detailBody{margin-right: 80px!important;}

#detailInn{margin-top: 45px!important;}

.sqbNote,.pageTopLink,.detailPrint{display:none!important}


/*add*/
.logo,.msthdlink,#msthd,li.tab-rank,.valueTossBtn,.socialBtn,#sqb_main{display:none!important}

#noteRecommend{padding-left: 100px!important;}

