css:
@-moz-document domain("quiltinghaven.com") {

body {
background: none !important;
background-color: #d1d1d1 !important;
color: #000 !important;
}

a {
color: #01348d !important;
}

#wrapper {
box-shadow: 3px 3px 10px #000;
border: 1px solid #526083;
}

#top_section {
background: none !important;
background-color: #687c9f !important;
height: 114px !important;

}

#header div.frame {
padding: 0 !important;
}

#header {
padding-left: 0 !important;
}

.forumtitle {
padding-left: 15px;
}

.catbg, h4.catbg span.left, h4.catbg2 span.left, h3.catbg span.left, h3.catbg2 span.left, .table_list tbody.header td span.left, .tp_topblock_frame, div.cat_bar, div.title_barIC, div.title_barIC h4.titlebg, .table_grid thead th  {
background: none !important;
background-color: #b5c1d9 !important;
}

#top_section:before {
content: 'Quilting Haven';
font-size: 40px;
margin: 20px;
line-height: 60px;
font-weight: bold;
text-shadow: #2a3960 5px 5px 5px;
}

.dropmenu li, .dropmenu li a.active, .dropmenu li a.active span.firstlevel, .dropmenu li a.firstlevel span.firstlevel, .dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel, .dropmenu li ul, .buttonlist ul li a span, .buttonlist ul li a, #forumposts .windowbg, .pagesection, .pagesection_top, .pagesection_bottom, ul.quickbuttons li, .buttonlist ul li a  {
background-image: none !important;
}

.dropmenu li, .buttonlist ul li a span,  ul.quickbuttons li a span {
background-image: none !important;
margin: 0 2px !important;
padding: 0 3px !important;
background-color: #b5c1d9 !important;
border: 1px solid #687c9f !important;
box-shadow: 1px 1px 1px #333;
}

.dropmenu li ul {
padding: 2px 0 0;
}

.table_list tbody.topheader td, .table_list tbody.bottomheader td, div.topheader h3.catbg, .pagesection, .pagesection_top, .pagesection_bottom {
background: none !important;
background-color: #687c9f !important;
}

.windowbg, #preview_body {
background-color: #e5ecf9 !important;
}

.button_submit, .button_reset {
background: none !important;
background-color: #b5c1d9 !important;
box-shadow: 1px 1px 1px #333;
}

.windowbg span.topslice, .windowbg span.topslice2, .windowbg span.topslice span, .windowbg span.topslice2 span, .windowbg span.botslice span, #recent span.botslice   {
background: none !important;
background-color: #687c9f !important;
}

.titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td, .catbg3, tr.catbg3 td, tr.catbg3 th, tr.catbg3 th.first_th {
background: none !important;
background-color: #687c9f !important;
}

div#main_content_section div#admin_content .windowbg .content, div#main_content_section div#admin_content .windowbg2 .content, .windowbg2 span.botslice, .windowbg2 span.topslice, .windowbg2 span.topslice2, .windowbg span.botslice, .windowbg span.topslice, .windowbg span.topslice2, #helpmain, .windowbg2 .content, .post_wrapper, form#postmodify span.upperframe, form#postmodify span.lowerframe  {
border-left: 1px solid #687c9f !important;
border-right: 1px solid #687c9f !important;
}

#main_admsection .windowbg span.botslice, #main_admsection .windowbg2 span.botslice, #admin_content .windowbg span.botslice, #admin_content .windowbg2 span.botslice {
border-bottom: 1px solid #687c9f !important;
}

.windowbg span.botslice, .windowbg span.botslice span {
background: none !important;
}

#registration .roundframe, #personal_messages .roundframe, .groupmembership .roundframe, #quickReplyOptions .roundframe, #simple_search .roundframe, #advanced_search .roundframe, .search_boards .roundframe, #forumposts span.botslice, form#postmodify span.lowerframe {
    border-bottom: 1px solid #687c9f !important;
}

div.cat_bar {
border: 1px solid #687c9f !important;
}

input, button, select, textarea {
background: none !important;
background-color: #e5ecf9 !important;
}

}
