css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("diamond.jp") {
#header,
#g-navi,
#service-menu,
#kanban,
#multipage-top,
.service-menu,
.article-end-listmenu,
#article-end-special,
#backtotop a,
#article-end-related,
#plus-selection-underline,
#article-end-todaytop,
#article-end-backnumber,
#article-end-underline,
#next-info,
#footer,
#sub-column{display:none!important;}

#article-content{
position:absolute;
top:0px;
left:390px;}

#title,
#main-contents{width:900px!important;}
#main-column{width:980px!important;}

#title h1{font-size:14px!important;
border:solid 0px;}
#main-contents{font-size:12px!important;}
div{font-size:10px!important;}


/*category page*/
#main-contents ul li h2,
#main-contents ul li p{font-size:10px!important;}
#column-top-content{
position:absolute;
top:0px;
left:390px;}
.category-summary{
position:absolute;
top:20px;
left:250px;
background:none;}
.category-title{
position:absolute;
top:-10px;
left:10px;}
.underline,
.category-more a{display:none!important;}


}
