css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("scroogle.org") {

 /* Global Style */ 

 :not(html):not(body)[bgcolor]

  {
    background-color: #222 !important;

  }

 [bordercolor]

  {

    border-color: #555 !important;

  }

  html, body

  {

    background: -moz-linear-gradient(center top , #352008, #7d4c13) repeat scroll 0 0 transparent !important;   
    background-attachment: fixed !important;
    color: #bbb !important;

    font-family: Arial, sans-serif !important;

    font-size: 10pt !important;

    margin: 0 !important;

    padding: 0 !important;

  }



  a

  {

    color: #58360d !important;

    padding-bottom: 2px !important;

    text-decoration: none !important;

  }

  a:hover

  {

    color: #999 !important;

  }



  a > b

  {

    color: #58360d !important;

  }

  a:hover > b

  {

    color: #999 !important;

  }



  a font

  {

    color: #aaa !important;

  }

  a:hover font

  {

    color: #333 !important;

  }



 /* small font links */

  font[size="1"] a

  {

    line-height: 1.75em !important;

  }



 /* he used <b> in place of <span> */

  b[style]

  {

    border: #000 0px solid !important;

    background-color:#bbb !important;

    line-height: 1em !important;

    margin: 0px !important;

    padding: 0 0px !important;

  }



  b[style="background-color: rgb(153, 255, 153);"],

  b[style="background-color: rgb(204, 255, 153);"]

  {

    background-image: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

  }

  b[style="background-color: rgb(160, 255, 255);"]

  {

    background-image: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

  }

  b[style="background-color: rgb(255, 255, 102);"],

  b[style="background-color: rgb(255, 255, 204);"]

  {

    background-image: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

  }

  b[style="background-color: rgb(255, 153, 153);"]

  {

    background-image: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

  }



  :not(a) > font

  {

    color: #777 !important;

  }

  :not(a) > font[size="2"]

  {

    color: #666 !important;

  }

  :not(a) > font > b

  {

    color: #333 !important;

  }



 /* forms */

 button,

 input,

 select,

 textarea

  {

   border: 1px solid #bbb !important;

   background-color: #444 !important;

   color: #ddd !important;

   margin: 2px !important;

   -moz-border-radius: 0 !important;

  }



 button,

 input[type="submit"]

  {

    background: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

    border-color: #000 !important;

    color: #000 !important;

  }

 input[type="password"],

 input[type="text"],

 input:not([type])

  {

    background: url(data:image/png;base64,) !important;

    background-repeat: repeat-x !important;

    border-color: #bbb !important;

    color: #292929 !important;

  }



 button:hover,

 input:hover,

 select:hover,

 textarea:hover

  {

   border: 1px outset #bbb !important;

  }

 select:hover,

 textarea:hover

  {

   background-color: #eee !important;

  }

 button:hover,

 input[type="radio"]:hover,

 input[type="submit"]:hover,

 select:hover

  {

   cursor: pointer !important;

  }



 button:active, 

 input:active, 

 select:active

  {

   background-color: #eee !important;

   border: 0px inset #bbb !important;

  }



}





@-moz-document url-prefix("http://www.scroogle.org/cgi-bin/nbbw.cgi"), 

 url-prefix("https://ssl.scroogle.org/cgi-bin/nbbwssl.cgi") 

{



 /* search pages */

  blockquote

  {

    background: #eee !important;
    border: solid #555 1px !important;

    margin: .5em !important;

    overflow: hidden !important;

    padding: 1em !important;
    padding-top: 5em !important;

    text-indent: -.5em !important;
    margin-top: -20px !important;

  }



 /* search results lists */

  blockquote ul

  {

    border-bottom: #333 1px dotted !important;

    color: #000 !important;

    margin: .5em .5em 2em .5em !important;
    margin-top: 0px !important;

    padding: .5em .5em 1em .5em !important;

  }



 /* search results separation */

  blockquote > font > b

  {

    border-color: #555 !important;

    border-style: dotted !important;

    border-width: 0 0 1px 0 !important;

    padding: .25em !important;

  }



 /* search form */

  blockquote > center > form

  {

    background: -moz-linear-gradient(center top , #393834, #333) repeat scroll 0 0 transparent !important;
    background-attachment: fixed !important;

    background-repeat: repeat-x !important;

    border: solid #000 1px !important;

    padding: .5em !important;

    margin: .5em -.75em -1.75em -.75em !important;

  }



 /* search results page, top form */

  blockquote > center:first-child > form

  {
    background:  -moz-linear-gradient(center top , #5a574e, #393834) repeat scroll 0 0 transparent !important;
    opacity:0.9 !important;
    box-shadow: 0 2px 10px rgba(0, 0, 0, 1.0) !important;
    position: fixed !important;
    border: solid #000 1px !important;

    padding: .5em !important;

    top: -8px !important;
    left: 19px !important;
    right: 19px !important;

  }



 /* search results header */

  center + font[face="Arial, Helvetica, sans-serif"] > b

  {

    border: #666 1px dashed !important;

    background: #111 !important;

    color: #666 !important;

    float: right !important;

    padding: .5em !important;

    margin: 0 !important;

    text-indent: 0 !important;

  }



 /* search results non-link urls */

  :not(a) > br + font[color="#008000"]

  {

    color: #555 !important;

    padding: 0 0 .25em 0 !important;

  }

  :not(a) > br + font[color="#008000"]:before

  {

    content: "http://" !important;

  }



 /* scraper.htm cartoons */

  center > font > font > img,

  center > font > font > a > img

  {

    margin-top: 50px !important;

  }



}
