css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* background */
body:not(.transparent_widget):not(.connect_widget),.showOnceInteracted, .uiWashLayoutWashContent {
 background:-moz-linear-gradient(top left, orange, purple, orange, purple, orange, purple) fixed
 !important;
background:-webkit-linear-gradient(top left, lightgreen, skyblue, violet, orange) fixed
 !important;
}

/* alpha background */
#contentCol, .UIIntentionalStory_Message, .UIStory, .profile_color_bar, #pageFooter, .uiSideNav, #left_column, .profile_info_container, .profile-friends, #rightcolumn, .fbxWelcomeBox, .single_photo_header, .fbGroupedNotifications, .navMoreLess{
 background-color:rgba(255, 255, 255, 0.3)
 !important;
}
.ufiItem, .uiMorePagerPrimary, .uiSideHeader, .uiHeaderNav, .uiHeaderSection, .uiBoxWhite, .uiBoxLightblue, .uiBoxGray {
background-color:rgba(171, 121, 165, 0.6) !important;
}
.uiStreamStory, .photocaption_text, .fbxWelcomeBox, .mbm:not(.uiHeader), .uiGrid, #pagelet_mutual {
-moz-transition-property: background-color;
  -moz-transition-duration: 0.4s;
  -moz-transition-timing-function: ease-out;
-webkit-transition-property: background-color;
  -webkit-transition-duration: 0.4s;
}
.uiStreamStory:hover, .photocaption_text:hover, .fbxWelcomeBox:hover, .mbm:hover:not(.uiHeader), .uiGrid:hover, #pagelet_mutual:hover {
background-color:rgba(255, 255, 255, 0.6) !important;
-moz-transition-property: background-color;
  -moz-transition-duration: 0.4s;
  -moz-transition-timing-function: ease-out;
-webkit-transition-property: background-color;
  -webkit-transition-duration: 0.4s;
}
#contentCurve {
display:none !important;
}
/* misc fixes */
.uiHeaderTopBorder {
border-top: none !important;
}
.navHeader, .navHeader a {
color: #222222 !important
}
}
