css:
#sidebar, #main {
background: #E5EEF5;
}

#top-menu {
padding: 8px 0 0 6px;
background: #375D81;
box-shadow: 0px 1px 3px black;
border-bottom: 1px solid #40698f;
}

#header {
background: #9fb5c9;
background-image: -webkit-linear-gradient(top, #183152, #375D81);
}

#main-menu {
border-top: 1px solid #e9f2fb;
border-bottom: 1px solid #8b9fb2;
background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);

}

#main-menu li a {
border-right: 1px solid #9fb5c9;
border-left: 1px solid #b4d3ee;
font-size: 11px;
padding: 8px 10px 8px 10px;
font-weight: bold;
background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);
text-shadow: 1px 1px 0px #628DAE;
}

#main-menu li a.selected,#main-menu li a:hover {
background-image: -webkit-linear-gradient(top, #97b4cf, #71a3d2);
border-right: 1px solid #658fb7;
}

h1 {
text-shadow: 1px 1px 5px #00253e;
}

.wiki h1 {
text-shadow: none;
}

h2 {
font-size: 22px;
color: #262626;
font-style: italic;
padding: 5px 0 0 0;
}

h3 {
font-size: 20px;
color: #262626;
}

#history h3 {
font-style: italic;
padding: 5px 0 0 0;
}

#sidebar h3 {
margin-top: 14px;
padding: 5px 10px;
font-size: 17px;
color: #fff;
text-shadow: 1px 1px 0px #628dae;
font-style: italic;
width: 100%;
background: #b8d2e7;
border-left: 5px solid #c5ddf0;
box-shadow: 0px 1px 1px #8b9fb2;
}

#sidebar a {
font-size: 12px;
line-height: 17px;
color: #367db4;
text-shadow: 1px 1px 0px #f4f9ff;
}


hr {
background: #B2CBD9;
}

table.attributes {
background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
border-radius: 4px;
border: none ;
padding: 10px;
box-shadow: 0px 1px 3px #8ca6c0;
}

div.issue {
border-radius: 4px;
background: #e9f2fb;
border: 1px solid #d9e1e8;
}

.contextual {
border-radius: 4px;
background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
padding: 2px 4px;
box-shadow: 0px 0px 3px #92b5d9;
margin: 4px;
}

.contextual:hover {
box-shadow: 0px 0px 1px #92b5d9;
}

.journal {

}

table.progress {
box-shadow: 0px 0px 1px #648bd2;
border-radius: 4px;
border: none;
}

table.progress td.closed {
border-radius: 4px 0 0 4px;
background-color: #A3BF3B;
background-image: -webkit-gradient(linear, left top, left bottom, from(#A3BF3B), to(#bddc4c));
}

td.priority {
font-weight: bold;
}


thead tr, thead th{
background: #dce6f3 !important;
text-shadow: 1px 1px 0px #f4f9ff;
color: #313131;
border: 1px solid #bfcad9;
}

tr.odd {
background: #eceff9;
}

tr {
height: 15px !important;
vertical-align: middle !important;
border-spacing: 5px;
}

.box {
background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
border-radius: 4px;
border: none ;
box-shadow: 0px 1px 2px #e5eaf2;
padding: 10px;
margin-bottom: 10px;
border: 1px solid #d2d8e1;
}

.box p {
line-height: 20px;
}

ul.toc {
background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
border: 1px solid #e5eaf3 !important;
font-size: 110%;
font-style: italic;
padding: 10px;
}

#footer {
background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);
text-shadow: 1px 1px 0px #628DAE;
}
