css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("7coollist.com") {


div.right,
#footer,
.bottomcover,
.comment-form,
div.cover > table,
.a2a_kit,
.postright > table,
#widget_x,
.news-block-magick,
.linkwithin_div,
#casing center table,
#top,
.avatar-image-container 
{ 
  display: none !important;
}

hr { display: none !important; }

#casing,
div.post,
#comments,
#comments > *,
#comments > * > *,
#comments > * > * > *,
#comments > * > * > * > *,
#comments > * > * > * > * > *
{
    background: #2D2626 !important;
}

* {
  border: none !important;
  text-shadow: none !important; 
}

#content {
  width: 100% !important;
}
#content img { 
  margin: 5px auto !important;
  display: block !important;
  border: none !important;
}



.post,
.comment-head
{
  color: #BCA0A0 !important;
}

.post a {
  color: #D3BBAA !important;
}
.post a:hover { text-decoration: underline !important; }
.post h5 {
  color: #E5CECE !important;
}

}
