css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.naturalpoint.com/optitrack"),
               url-prefix("https://www.naturalpoint.com/optitrack") {

/* # = id, . = class */

/* ---- BEGIN "responsive design" section updates  ---- */
header.row {
  background: -webkit-linear-gradient(#f9f9f9, #b7b7b7) !important;
  background: -moz-linear-gradient(#f9f9f9, #b7b7b7) repeat scroll 0 0 #232323 !important;
  -webkit-box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.12);
  -moz-box-shadow:    0px 3px 4px rgba(0, 0, 0, 0.12);
  box-shadow:         0px 3px 4px rgba(0, 0, 0, 0.12);
  }

/* darken nav font and increase text size */
a.global-nav-menu-link {
  font-size: 12px  !important;
  /*  font-weight: bold !important; */
  color: #505050  !important;
  color: rgba(0, 0, 0, 0.70) !important;
  /*  letter-spacing: 0.10em !important; */
  text-shadow:none !important;
  }


/* change color and move menu nubs to the right of text instead of below */
.mega-menu-added:after {
  background-color: rgba(255, 255, 255, 0.25) !important;
  color: rgba(0, 0, 0, 0.50) !important;
  content: "â–¾";
  text-align: center;
  width: 1em;
  margin: 0px 5px !important;
  display: inline-block !important;
  }


#primaryNav .dropdown {
  margin-left: -80px !important;
  top: 30px !important;
  z-index: 11 !important; /* float menu one layer above cart */
  }


/* remove extra padding at top of menu */
.global-nav-controls > ul {
  padding-top: 0px !important;
  }

/* cart : move to below nav area */
.goto-global-cart {
  position:absolute !important;
  top: 44px         !important; 
  margin-right:50px !important;
  /*  left: 775px       !important; */
  z-index: 10       !important; /* push above crux so its clickable */
  width:100px       !important;
  }

/* cart : lighten background color */
.goto-global-cart {
  background-color: #B0B0B0 !important;
  }

nav#breadcrumbs {
  position:absolute !important;
  top: 5px         !important;
  left: 5px        !important;
  }



/* remove logo background, make semi transparent and fix location */

/* logo : remove red background color, shadows, etc */
#optitrack-logo {
  /* background-color: rgba(0, 0, 0, 0.15) !important; */
  background-color: rgba(0, 0, 0, 0.0) !important;
  background-image: none !important;
  border-radius: 0 0 0 0 !important;
  box-shadow: none !important;
  }

/* logo : fade logo image out a little */ 
#optitrack-logo > a {
  opacity:0.75; 
  }
  
/* logo : remove top and bottom padding */ 
#optitrack-logo a {
  /*    display: block; */
  padding: 0 10px 0 30px !important;
  }
  
/* logo : remove left-edge padding */
nav.global-nav-controls {
  width: 14% !important;
  }
 
/* menu : trim space at top */
.global-nav-menu, .global-nav-menu.closed, global-nav-search, .global-nav-search.closed {
  margin-top: 0px !important;
  }
    
/* shrink down search box width */
.global-nav-search, .global-nav-search.closed {
  width: auto !important;
  }



/* ---- END "responsive design" section updates  ---- */



/* move cart below nav area */
#utilities {
  position:absolute !important;
  top: 49px         !important; 
  left: 775px       !important;
  z-index: 10        !important;  /* push above crux so its clickable */
/*  margin-top: 27px  !important; */
  }

/* move cart below nav area from bottom of page */
#breadcrumbs {
  position:absolute !important;
  top: 35px         !important;
  left: 22px        !important;
/*  margin-top: 45px  !important; */
  }

/* nav area 100% page width */
.container{
  width: 100% !important;
  }

#homepage .unified.quicklinks,
#homepage .crux,
#secondaryNav {
  width: 950px !important;
  margin-left:auto !important;
  margin-right:auto  !important;
}

.showcase {
  margin-left : auto !important;
  margin-right: auto !important;
  margin-top  : 40px !important; 
  }

/* background and drop shadow for nav area */
#primaryNav {
/*   border-bottom: 1px solid #b3b3b3 !important; */
  background: -webkit-linear-gradient(#f9f9f9, #b7b7b7) !important;
  background: -moz-linear-gradient(#f9f9f9, #b7b7b7) repeat scroll 0 0 #232323 !important;
  -webkit-box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.12);
  -moz-box-shadow:    0px 3px 4px rgba(0, 0, 0, 0.12);
  box-shadow:         0px 3px 4px rgba(0, 0, 0, 0.12);
  }


/* darken nav font and increase text size */
#primaryNav ul.tencol > li > a {
  font-size: 12px  !important;
  /*  font-weight: bold !important; */
  color: #505050  !important;
  color: rgba(0, 0, 0, 0.70) !important;
  /*  letter-spacing: 0.10em !important; */
  }

/* move dropdown menu nubs next to text instead of below */
#primaryNav .mega {  
  display: inline-block !important;
  margin-top: 0px !important;
}
#primaryNav .expand  {
    background: url("http://www.naturalpoint.com/optitrack/static/img/arrowBoxesDownBlack.png") no-repeat scroll center -44px transparent !important;
    opacity:0.75;
/*  display: inline-block !important; */
  margin-top: 10px !important;
}
#primaryNav .dropdown
{
  margin-left: -80px !important;
  top: 30px !important;
  z-index: 11 !important; /* float menu one layer above cart */
}

/* compress nav menu height */
#primaryNav ul.tencol {
  padding-top: 2px !important;
  margin-bottom: 10px !important;
  }

/* move search area down to align with menu*/
#search {
  /* to do */
  }

/* remove logo background, make semi transparent and fix location */
#optitrackLogo {
  position:absolute !important;
  top:-2px          !important; 
  left:30px         !important;
}
#optitrackLogo > a {
  background:none   !important;

  }

/* extra padding between content top and nav/breadcrumbs */
.crux {
  padding-top: 25px !important;
}


/* make it more apparent that tabbed sub menus near page bottoms are tabs */
.bubble li {
    border-radius: 10px 10px 1px 1px !important;
    padding: 3px 10px 9px 10px  !important;
    }

.unified.quicklinks ul.nav {
  border-color: rgba(0, 0, 0, 0.3)  !important;
  }

/* End of style */
}
