@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("otvet.mail.ru") {

	* {
		border-radius: 0.4em  !important;
		font-family: DejaVu Sans, Tahoma !important ;
	}

    a.nav-header__link:nth-child(2),
    a.nav-header__link:nth-child(3),
	#Stats,
    .OrFrame,
    .DayLead,
    .trg-b-list,
    .torg_mail_ru__200x300,
    .logoWhite3Head,
    .question-leaders,
    .category-leaders,
    .torg-answer-banner__content-wrapper 
    {display: none !important}

	h,h1,h2,h3,h4,h5,[class*="orange"], #questext *, .questext {
		color: hsl(60,80%,60%) !important;
/* 		text-shadow: -0.1em 0.1em 0.05em hsl(210,50%,70%) !important */
	}

	.answ-now-top,.answ-now-last,button,#button{
/* 		background: linear-gradient(90deg, hsla(220,100%,50%,.3),hsla(60,100%,50%,1),hsla(220,100%,50%,.3)) !important; */
        background: hsla(210,70%,50%,1) !important;
        
	}

	body>*, body, .Answ, .Quesstions *
    ,#menu, menu, .page, .column, 
    #portal-headline {
        border: hsl(210,50%,50%) !important;
		background: linear-gradient(45deg, hsl(210,50%,70%),hsl(210,40%,80%)) !important;
		color: hsl(210,70%,20%) !important;
		/*border: 0.5px solid !important;  */
		/* text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important; */
		
	}

	label,#label{
		/* background: hsl(210,30%,70%) !important; */
		color: hsl(210,70%,20%) !important;
		/* border: hsl(210,30%,50%) !important; */
		text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important;
	} 

	a:visited{
		color: hsl(210,70%,0%) !important;
	}

	a:hover{
		background: hsl(210,40%,80%) !important;
		color: hsl(210,100%,50%) !important;
	}

	a:link, a:active{
		/* background: hsl(210,60%,70%) !important; */
/* 		color: hsl(210,100%,30%) !important; */
	}

	input,text,#input,#text,edit,#edit,message,#message,option,#option,select,#select,.form--text{
		background: hsl(210, 30%, 70%) !important;
		color: hsl(210, 70%, 20%) !important;
		border: hsl(210,30%,50%) !important;
/* 		text-shadow: 0.1em 0.1em 0.05em hsl(210,30%,70%) !important; */
		font-family: DejaVu Sans !important ;
	}
}
