css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.foopets.com") {

body{background:#af00ff url('http://i.imgur.com/IV6Td.png ') repeat-x 0 0!important}

#nav{position:absolute;top:30px;left:50%;z-index:2;background:url(http://i.imgur.com/Y80X7.png) no-repeat 0 0;width:870px;height:95px;
  margin:0 0 0 -435px;padding:0;list-style:none;!important}

#page{
  border-bottom:1px solid #ddd;
  background-color:#00ff00;
  width:830px;
  height:auto;
  min-height:300px;
  margin:76px auto 0 auto;
  padding:30px;
  overflow:hidden;
!important}
#nav li a:hover,#nav li a:focus,#nav li a:active{
  background: #ffff90;/* old browsers */
  background: -moz-linear-gradient(top, #ffffc8 0%, #ffffab 50%, #ffff93 50%, #ffff71 100%); /* firefox */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffc8), color-stop(50%,#ffffab), color-stop(50%,#ffff93), color-stop(100%,#ffff71)); /* webkit */
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffc8', endColorstr='#ffff71');
!important}
#nav li.selected a{
  background: #ffff00;/* old browsers */
  background: -moz-linear-gradient(top, #ffffae 0%, #ffff6f 50%, #ffff49 50%, #ffff00 100%); /* firefox */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffae), color-stop(50%,#ffff6f), color-stop(50%,#ffff49), color-stop(100%,#ffff00)); /* webkit */
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffae', endColorstr='#ffff00');
!important}

#subnav ul li{
    font-size:13px;
    height:28px;
    line-height:28px;
    padding:0;
    margin:0;
    border-right:1px solid #7200ab;
    border-left:1px solid #e3abff;
    background: #aa00ff;/* old browsers */
    background: -moz-linear-gradient(top, #aa00ff 0%, #b219ff 50%, #bd38ff 50%, #c550ff 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#aa00ff), color-stop(50%,#b219ff), color-stop(50%,#bd38ff), color-stop(100%,#c550ff)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#aa00ff', endColorstr='#c550ff');
  !important}

  #subnav ul li:hover{
    background: #00bdff;/* old browsers */
    background: -moz-linear-gradient(top, #0077ff 0%, #009dff 50%, #00b9ff 50%, #00edff 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#0077ff), color-stop(50%,#009dff), color-stop(50%,#00b9ff), color-stop(100%,#00edff)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#0077ff', endColorstr='#00edff');
  !important}
  #subnav ul li.selected{
    background: #ffff00;/* old browsers */
    background: -moz-linear-gradient(top, #ffffae 0%, #ffff6f 50%, #ffff49 50%, #ffff00 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffae), color-stop(50%,#ffff6f), color-stop(50%,#ffff49), color-stop(100%,#ffff00)); /* webkit */
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffae', endColorstr='#ffff00');
  !important}

}
