css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://ja.wikibooks.org/"){

li   {  
        font-size: 10pt !important;
        letter-spacing:1px;
        text-decoration: none !important;

}

P   {   font-family: Candara, georgia;
        font-size: 11pt;
        letter-spacing:1px;
        text-decoration: none ;
        padding:20px 80px 0 40px;}

.thumbinner {margin:10px 20px 10px 20px }

.firstHeading {
        font-size:50px !important;
        font-family: georgia !important;
        font-style:italic ;
        font-weight:bold !important;
        color:#c5bc98 !important;
        padding-bottom:10px !important;
        margin-bottom:70px !important;
        text-shadow: 1px 1px 1px #666;
        text-align:center;}

.mw-headline {
        font-family: georgia !important;
        font-style:italic ;
        color: #333 !important;}

h3 {margin-top:40px !important;}

h2 {padding-top:50px !important;}

#mw-panel,#mw-head,#footer,#mw-head-base,#mw-page-base {display:none !important;}

#content {
-webkit-box-shadow: 0px 0px 80px #999;
-moz-box-shadow: 0px 0px 80px #999;
box-shadow: 0px 0px 80px #999;
margin:0px auto !important; 
width: 700px !important;
padding:40px !important;
background: -webkit-gradient(
    linear,
    left top,
    left bottom,
    color-stop(0.0, rgb(250,248,220)),
    color-stop(0.05, rgb(255,255,255)),
    color-stop(0.6, rgb(250,248,220))
)!important;
background:-o-linear-gradient(
    center top,
    rgb(250,248,225) 0%,
    rgb(255,255,255) 5%,
    rgb(250,248,225) 60%
)!important;
}

.toclevel-1 {line-height:22px !important;}

table {
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px;margin:5px;
}


.thumbcaption {margin:5px;}

.ambox {margin-bottom:60px !important;}

.infobox {padding:10px !important; 
margin-left:30px !important;
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px !important;
margin:5px; 
line-height:15px !important;}

.infobox img {margin:15px !important;}

.vertical-navbox {
padding:10px !important; 
margin-left:30px !important;
font-size: 8pt !important;        
font-family: tahoma !important;
letter-spacing:1px !important;
margin:5px; 
line-height:15px !important;}

.mediawiki {
background-image: none !important;
background-color:#dfddc8 !important;}
}

/* Table of Contents Box == Icon - http://p.yusukekamiyamane.com/ */

#content #toc {

background: transparent url(data:image/png;base64,) no-repeat !important;

position: fixed !important;
top: 5px !important; left: 5px !important;
height: 16px !important;
width: 16px !important;
display: block !important;
border: none !important;
overflow: hidden !important;
z-index: 9999 !important;
text-indent: -9999px !important;
padding: 5px !important;
margin: 0 !important;
opacity: .6 !important;
}

#content #toc:hover {

display: table !important;
background-image: none !important;
width: auto !important;
height: auto !important;
background-color: rgba(0,0,0,.82) !important;
outline: 2px solid rgba(180,180,180,.82) !important;
text-indent: 0 !important;
opacity: 1 !important;
}

/* ToC text styling */

#content #toc H2 {color: #EEE !important;}
#content #toc a  {color: #BACFF2 !important;}
#content #toc a:visited {color: #DDC6F2 !important;}
#content #toc a:hover {color: #FFE900 !important;}

#toc .toctoggle {display: none !important;}
.editsection,
#localNotice P{display: none !important;}
