/*style erstellt von peter pech cc-license for private only */

@-moz-document domain('picasaweb.google.com') {

html, body, .gphoto-topnav-contents, .lhcl_content {
background-color: #257A43 !important;
}
.gphoto-topnav-contents a {
background-color: #257A43 !important;
} 
a, .lhcl_searchoption_unselected  {
color: #EBF766 !important
}
.gphoto-logo img {display: none !important;}
.gphoto-logo {background-color: transparent !important; background-image: url('data:image/gif;base64,') !important;
 width: 137px !important; height: 47px !important;background-position: 50% 50% !important; background-repeat: no-repeat !important;
}
.gphoto-explore-widgets {margin-left: auto !important; margin-right: auto !important; width: 90% !important;}
/*a#lhid_logo {visibility: hidden !important;}*/
/*colors lighter Green */
#gbx4, .lhcl_contextbar, .lhcl_tray, .lhcl_sidebar, .gphoto-topnav  {background-image: none !important;
background-color: #9DD3B0 !important;
}
.lhcl_contextbar a, .lhcl_tray a {
color: #33317A !important;
}
.lhcl_toolbar {background-color: #9DD3B0 !important}
.gphoto-context-current {color: #ffffff !important;}
/* alben */
#lhid_rightbox {padding-left: 5px !important;}
.goog-icon-list-icon-link {
border-width: 4px !important; border-color: #9DD3B0 !important;
border-radius: 5px !important; border-style: inset !important; border-spacing:2px !important;
}

.gphoto-album-cover.goog-inline-block {width: 175px !important;}

.gphoto-album-cover-frame {
height: 100px !important; border: 4px inset #6DAB83 !important; border-radius: 5px !important;
}
.SPRITE_shadow, .SPRITE_shadow {
background-color: transparent !important; background-image: none !important;
height: 110px !important;
}
.gphoto-album-cover-title {display: block !important; height: 16px !important; overflow: hidden !important ; width: 160px !important;}
.gphoto-album-cover-date {display: inline-block !important; width: 70px !important;}
.gphoto-album-cover-photocount {display: inline-block !important; width: 80px !important; float: right !important; }

#lhid_leftbox,#lhid_slider,.lhcl_slider {
background-color: #125A2C !important; border-spacing: 0px !important; background-clip: none !important; background-image: none !important;
}

.gphoto-context-display {color: #ffffff !important;}

#lhid_trayhandle {
background-color: #257A43 !important;
} 
.lhcl_googlephotos_body #lhid_albumprop { 
background-color: transparent !important;
color: #372B0C!important;}

/* misc tweaks */
.pagerfirst, .pagercurrent,.pagerpage,.pagernext,.pagerprev  {
background-image: none !important; height: 0px !important;
}
.goog-icon-list-searchicon-meta-title, .gphoto-comment, .gphoto-comment-info-text {
color: #ffffff !important;font-style: italic !important;
}
div.gphoto-comment-hoverable:hover {background-color: #6DAB83 !important; background-image: none !important; color: black !important;}
#lhid_emailform {background-color: #6DAB83 !important;}
}
