css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geekologie.com") {

#head-leaderboard,
#navbar-wrapper,
.widget-header clearfix,
 #leaderboard,
 #footerleaderboard,
 #rrelated-stories,
 #menu-archive a,
 #menu-tips a,
 #menu-home a,
 #menu-geek a,
 #menu-movies a,
 #menu-celeb a,
 #network-bar-wrapper,
 #head-leaderboard,
 #rectangle-side,
 #navigation-bar-wrapper,
 #facebook,
 #network-footer,
 #footer,
 #entry-navigation,
 #navbar-wrapper,
 #skinny-bar,
 .excerpt-entry,
 .like-buttons,
 .related,
 .widget-header,
 .widget-header-txt,
 .tweet,
 .tweet-wrapper,
 .fb-like-box,
 .fb_iframe_widget ,
 .connect_top clearfix,
 .ad-top-leaderboard,
 .entry-container-advertorial,
 .sidebar-title,
 .rectangle-container,
 .module-wrapper-plain,
 .admoduleB,
 .admodule,
 .skyscraper,
 .ad-top-rectangle,
 .adspot_content,
 .adspot_link,
 .adspot,
 .moduleB,
 .module,
 .story-count,
 .rectanglead
     {display: none !important;}

#header {
    border-bottom: solid 5px #111 !important; 
}

#ccontent {
	width:1000px;
	background: none !important;
	color: #888 !important;
	line-height: 1.8 !important;
	margin: 10px auto !important;
	padding-top: 20px !important;
	overflow:auto;
	border: none !important;
}
#container {
	background: #222 !important;
	border-top:solid 3px #333 !important;
	line-height: 1.9 !important;
	border: none !important;

}

#header-wrapper {
	width: 1000px !important;
	margin:0 auto;
	height:170px;
	/* background: none !important; */
	display: block;
	border-top: none !important;  
	border-bottom: solid 1px #333 !important;  
}
#sidebar .txt_holder {
	font-family: helvetica, "Arial", Times, sans-serif !important;
	font-size: 14px !important;
	text-align:center;
	width:258px; 
	padding:10px;
	margin:-25px 10px 0 10px;
	position:relative;
	background: #444 !important;
	border:solid 2px #353535 !important;
	border-top:solid 2px #f06eaa;
	line-height:1.2;
}
blockquote, q { 
    quotes:none; 
    padding:15px; 
    background: #353535 !important; 
    margin:15px; 
    border-left:solid 0px #ccc !important; 
    line-height:1.9;
}
.page-navigation-center {
    border: none !important;
}
.module-content {
   font-size:12px;
   padding:8px 10px;
   line-height:1.6em;
   border: solid 1px #333 !important;
   list-style-type:none; 
   list-style-position:outside; 
   font-family:Arial, Helvetica, sans-serif;
   overflow:hidden;
}
.content-header {
   background: #333 !important;
   border-bottom: solid 2px #555 !important;
}
body{

        background-attachment: fixed !important;
        background-color:#000000 !important;
        margin: auto !important;
        line-height: 1.9 !important;
        font-size: 15px !important;
        font-family: helvetica, "Arial", Times, sans-serif !important;
}
.sidethumb {
   font-size:12px;
   padding:8px 10px;
   line-height:1.6em;
   border:solid 1px #333 !important;
   font-family:Arial, Helvetica, sans-serif;
   overflow:hidden;
   }
#navbar {
	height:170px;
	list-style:none;
	display:inline;
	overflow:hidden;
	width:1000px;
	background: none !important;
}
#menu-logo a{
        position: relative !important;
        left: 248px !important;
}
.module-wrapper {
    background: none !important;
    padding-left: 38px !important;
    padding-right: 0px !important;
}
#leaderboard-wrapper {
    height: 1px !important;
    background: #333 !important;
	border: none !important;
	display: block !important;
	padding: 0px !important;
}
#posts h1 a {
        color: #999 !important; 
    	line-height: 1.3em !important;
} 
.entry {
	line-height: 1.9 !important;
	width: 640px !important;
	padding:20px 20px 20px 20px !important;
	border-bottom: solid 2px #444 !important; 
	background: #222 !important;
	margin-bottom: 30px !important;
}

#sidebar {
	background: none !important;
	border: none !important;
	margin-top: 0px !important;
	padding-top: 0px !important;
}
.text_holder {
	background: white !important;
}
#posts blockquote {
    color: #888 !important;
	background: #252525 !important;
}
#posts {
	border-top: none !important;
}
a {color: #d6d6d6 !important; text-decoration:none;}
a img {border:0;}
a:hover {color: #fff !important; text-decoration:underline;}

.tags-entry {
	border-bottom: solid 1px #333 !important;
	margin-bottom: 15px;
	margin-top:15px !important;
}
#footer-wrapper {
	background: #222 !important;
}
.featured-entry {
    width: 100% !important;
}
.widget {
    width: 100% !important;
    overflow: hidden !important;
}
}
