css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rememberthemilk.com") {
  #detailstitle_span {
    line-height: 1.66em;
  }
  #listbox {
    width: 70% !important;
  }
  #detailsbox {
    width: 30% !important;
  }
  .xtd_check {
    padding-right: 6px !important;
  }
  .xtr td {
    background-image: -moz-linear-gradient(#f6f6f6, #f0f0f0) !important;
    padding-top: 4px !important;
    padding-bottom: 8px !important;
    line-height: 1.66em;
  }
  .xtd_tag {
    display: block;
    margin-left: 0px !important;
  }
  .xtd_task_name {
    display: block;
    margin-left: 12px !important;
  }
  a:link {
    color: #0060c1;
  }
  #listtabs .xtab_selected, #listtabs .xtab_selected a {
    background-color: #0060c1 !important;
  }
  #listtabs .xtab_selected:after {
    border-left: 5px solid #0060c1 !important;
  }
  
  /*
   * Content Box
   */
  #content {
    width: 1280px !important;
  }
  
  
  /*
   * Header
   */
  #statusbox {
    height: 3em !important;
  }
  
  
  /*
   * Tabs
   */
  .xtabs {
    background: none !important;
  }
  .xtabs ul {
    padding: 0 !important;
  }
  .xtabs li {
    border-top: 1px solid #f0f0f0 !important;
    border-left: 1px solid #cacaca !important;
    border-right: 1px solid #cacaca !important;
    padding: 0 !important;
  }
  .xtabs li.xtab_selected, .xtabs li.xtab_selected a {
    background-color: #0060c1 !important;
    background-image: -moz-linear-gradient(#1676d7, #0060c1) !important;
    color: #fff !important;
  }
  .xtabs li, .xtabs li.xtab_smartlist {
    float: left !important;
	background-color: #F0F0F0 !important;
    background-image: -moz-linear-gradient(#F6F6F6, #F0F0F0) !important;
    padding: 0 2px 0 9px !important;
  }
  .xtr.xtr_hover td, .xtr.xtr_hover .xtd_arrj {
    background-image: -moz-linear-gradient(#F0F0F0, #ececec) !important;
  }
  .xtabs a, .xtabs li.xtab_smartlist a {
    background: none !important;
    color: #333 !important;
    font-weight: normal !important;
  }
  
  
  /*
   * Lists & Tasks
   */
  #list {
    width: 910px !important;
  }
  #listwrap {
    border-top: 1px solid #cacaca !important;
  }
  
  
  /*
   * List Tabs
   */
  #listwrap {
    float: left !important;
    width: 74% !important;
    margin-bottom: 200px !important;
    border-top: 1px solid #cacaca !important;
  }
  #listtabs {
    width: 20% !important;
    float: left !important;
    margin-right: 27px !important;
  }
  #listtabs > ul > li:nth-child(1) {
    border-top: 1px solid #cacaca !important;
  }
  #listtabs li {
    float: none !important;
    position: relative !important;
    padding: 0 4px 3px 10px !important;
  }
  
  
  /*
   * Tasks
   */
  .xtr.xtr_select .xtd {
    background-image: -moz-linear-gradient(#E0ECF2,#BFDCEB) !important;
  }
  
  
  /*
   * Details
   */
  #details {
    border-top: 1px solid #cacaca !important;
  }
}
