css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('https://www.google.com/reader/'), url-prefix('http://www.google.com/reader/'){

    /* Removing 2 Top Ribbons */
    
    #gb{
        display: none;
    }
    
    /* Sidebar - Subscriptions list */
    
    .scroll-tree .icon, .scroll-tree .favicon{
    	width: 19px !important;
    }
    .scroll-tree .folder-icon{
    	background-position: -46px 4px !important;
    }
    
    /* Items */
    
    #title-and-status-holder{
    	background-color: #F3F3F3;
    }
    
    #title-and-status-holder #chrome-title,
    #title-and-status-holder #chrome-title a{
    	padding: 10px 0 !important;
    	font-size: 22px !important;
    	color: #D44836 !important;
    }
    
    .entry-source-title{
    	color: #99F !important;
    }
    	
    #entries.list .read .collapsed{
        background-color: #DDF !important;
    }

}
