css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forbes.com") {
body{
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
}

html.js body#articletemplate div#contentwrapper section#abovefold.clearfix{
background-color: #E0DFDB !important;
padding:10px !important;
border:1px solid #999 !important;
}
div.article_head hgroup h1{
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
font-weight: bold !important;
font-size:2em !important;
line-height:130% !important;
color:#444 !important;
}
div#leftRail.fleft div.body p{
font-size: 1.2em !important;
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
line-height:140%;
color:#444 !important;
}

html.js body#articletemplate div#contentwrapper section#abovefold.clearfix div.fleft div#leftRail.fleft div.body li{
font-size: 16px !important;
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
font-weight: normal;
line-height:140%;
color:#444 !important;
color:#666 !important;
}
html.js body#articletemplate div.globalwrapper header div.headerwrapper nav#primnav.clearfix ul#header_topnav li, html.js body#articletemplate div.globalwrapper header div.headerwrapper div#header_promos.clearfix a, html.js body#articletemplate div.globalwrapper header div.headerwrapper div#header_masthead.clearfix div#header_magazines div#header_subscriber{
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
}
}
