css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.escapistmagazine.com/videos/view/zero-punctuation") {*{/*$*/}
    * { border: none !important; }
	html, body, #site_body, .video_box, .video_box_content {
		background:				#101010 !important;
		color:					rgba(200,200,200,1) !important;
	}
	#container, #main_column, #content, .video_box {
		width:					1280px !important;
		margin:					0 auto !important;
	}
	#gallery_display, #recent_gallery, #video_details {
		background:				#202020 !important;
	}
	.name {
		color:					rgba(200,200,200,1) !important;
	}
	.date, .comments a, .by {
		color:					rgba(160,160,160,1) !important;
	}

	embed, #video_player_object, #player_api, object {
		background:				#101010 !important;
		width:					1280px !important;
		height:					746px !important;
	}

    div.filmstrip_video {
        float:                  left !important;
        height:                 166px !important;
    }

    br[clear="all"] {
        clear:                  none !important;
        display:                none !important;
    }

	div.video_box_content.site_panel {
		width:					770px !important;
		margin:					0 auto !important;
	}

	#submenu, .submenu_spacer, #recent_site, #right_column, #site_header, #tabbed_menu, #user_panel, #legal, .legal_notes, .video_closing, .video_box_content a[target="_blank"], div[style="height: 455px; width: 195px; position: fixed; top: 0px; left: 0px;"], .footer, .share_panel, .fb_iframe_widget, .gallery_description, .video_box_content br[clear="all"], #recent_gallery, #login_panel, #site_menu, #articles_top_nav, #sponsor_header, #related_video_panel, .tags, #video_player_menu, #video_navigation, h1.headline>a, .by, #taboola-bottom-main-column, #video_details {
		display:				none !important;
	}

    h1.headline {
        padding-left:           8px !important;
    }

	#sponsor_display, #sponsor_player_container, #sponsor_player {
		background:				transparent !important;
		width:					964px !important;
		height:					574px !important;
	}
	#sponsor_player {
		position:				relative !important;
			top:				0 !important;
			left:				0 !important;
	}
}
