@-moz-document url-prefix('http://www.tumblr.com/') {

*{text-shadow:none !important; color:#666666 !important;}{font:16px 'Trebuchet MS';

color:#666666;

}



#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_tumblelog, #dashboard_inbox, #dashboard_help, 









#dashboard_preferences, #dashboard_followers, #dashboard_members, #dashboard_tumblelog, #dashboard_inbox, 

#dashboard_help{background: url('http://media.tumblr.com/tumblr_luhga3Noe31qhg011.jpg') bottom right #d9d1de no-repeat fixed !important; background-size: 100%}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#c692d8!important; background: rgba(105,105,105,0.3) !important;}

ol#posts li.notification{background-color: #c692d8 !important; border-bottom: 2px solid #9d92a5 !important; color: #c692d8 !important;opacity: 0.50;}

#right_column a#dashboard_switch_blog_menu_current:hover {background:#c692d8;opacity: 0.50;}

#nav .nav_item.active{background-color:#c692d8}

#left_column #posts .post .permalink{background-color:#c692d8;opacity: 0.50;}

#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#c692d8 !important;

background-image:none !important; width:100%; height:100%;

border:0px !important}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{background-image:none !important}

.notification_type_icon{display:none !important}

#left_column #posts .notification {

background-color:#c692d8 !important;

color:#666 !important;

border-bottom-color: #eee !important;opacity: 0.50;}

#new_post_notice_container{

color:#fff !important}

#new_post_notice_container a{

color:#fff !important}

ol#posts li.post.new_post {



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;}

}





