css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yucasee.jp") {
.rectangle,#rankingPR,#member,#RSSfeed,#Twitterfeed,
.efootTwitter,.centerbox,.outgoing,#main div table,#main div embed,#footer,#FBbox,#side > p,.quickQ, .aside h4,.pr,#melmaga,.outgoing_article,.feedback
{display:none;}
.tools,#side .minicolum,#hnEnt{display:none!important;}

.article_body{font-size:97%!important;}

#header{height:40px!important;
top:-60px;}
form{
position: absolute;
top:50px;
left:20px;
padding-left: 0px!important;}
.headbg{background-position:-16px -50px!important;}




/* Table of Contents Box == Icon - http://p.yusukekamiyamane.com/ */

.kanren {

background: transparent url(data:image/png;base64,) no-repeat !important;

position: fixed !important;
top: 5px !important; left: 5px !important;
height: 16px !important;
width: 16px !important;
display: block !important;
border: none !important;
overflow: hidden !important;
z-index: 9999 !important;
text-indent: -9999px !important;
padding: 5px !important;
margin: 0 !important;
opacity: .6 !important;
}

#main .kanren:hover {

display: table !important;
background-image: none !important;
width: auto !important;
height: auto !important;
background-color: rgba(0,0,0,.82) !important;
outline: 2px solid rgba(180,180,180,.82) !important;
text-indent: 0 !important;
opacity: 1 !important;
}

/* ToC text styling */

#main .kanren H2 {color: #EEE !important;}
#main .kanren a  {color: #BACFF2 !important;}
#main .kanren a:visited {color: #DDC6F2 !important;}
#main .kanren a:hover {color: #FFE900 !important;}


.kanren .toctoggle {display: none !important;}
.kanren li{
padding-top: 0px!important;
padding-bottom: 0px!important;
border-bottom-width: 0px!important;
}

}
