css:
@-moz-document url-prefix("http://www.neogaf.com") {
/* NeoGAF Dark Pro */
/* by sky */

#main, .goodie728, .goodie300{
background: #0f1013 !important; 
}
#header{
background: -moz-linear-gradient(top,  #222222,  #0f1013) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#222222), to(#0f1013)) !important;
}
#toolbar, #toolbar{
background: #191919 !important;
color: #7d7f84 !important;
}
#headeruser, #headeruser a{
border-color: #444444 !important;
color: #a3a6c1 !important;
}
#headernav > li > a {
color:#7d7f84 !important; 
}
#headernav > li.active > a {
color: #aeaeae !important; 
}
#headernav > li:hover > a:hover,
#headernav > li.active:hover > a:hover,
#headeruser > .headeruserbit:hover > a,
#headeruser > a:hover{
color: #FF8B01 !important;
}
body, .postbit-details{
color: #aeaeae !important;
}
.tcat, .tcat-tools, .thead, .pagenav > li.current > span, .pagenav > li > a:hover,  .tfoot {
background: -moz-linear-gradient(top,  #3c414d,  #2f343d) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;
color: #aeaeae !important;
text-shadow: 0px 1px 0px #111 !important;
}
.tcat a, .tcat a:visited, .tcat a:active{
 color: #aeaeae !important;
}
.pagenav > li > a:hover {
color: #FF8B01 !important;
}
.tcat-tools > a{
 border:0px !important;
}
.alt1, .alt1Active, .alt2, .alt2Active, .postbit-details{
background: #252628 !important;
border-color: #333333 !important;
}
.alt2, .postbit-post, .post-meta-border{
background: #252628 !important;
border-color: #333333 !important;
}
blockquote.quote{
background: #2f343d !important;
border-color: #3c414d !important;
box-shadow: -0px 2px 2px #111;
}
a:link, a:active {
color: #a3a6c1 !important;
}
a:hover, a:visited:hover {
color: #FF8B01 !important;
}
a:visited {
color:#7d7f84 !important;
}
#links a:hover {
color: #FF8B01 !important;
}
.thread-prefix {
background: #666666 !important;
border:1px !important;
border-style:solid !important;
border-color: #999999  !important;
color: #cccccc !important;
text-shadow: 0px 1px 0px #222 !important;
}
.postbit {
border-color: #333333 !important;
}
#footer {
background: #0f1013 !important;
}
.spoiler{
border: 1px !important;
border-color:#888888 !important;
border-style: solid !important;
border-radius: 3px !important;
box-shadow: -0px 2px 2px #111;
}
.spoiler:hover, .spoiler:active, .spoiler.show{
color: #FF8B01 !important;
background: none !important;
text-shadow: 1px 1px 2px #222 !important;
border: 0px !important;
box-shadow: 0px 0px 0px #222;
}
/* Buttons */
.large-button, a.large-button {
background: -moz-linear-gradient(top,  #555555,  #333333) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#333333)) !important;
text-shadow: 0px 1px 0px #111 !important;
color: #aeaeae !important;
font-weight: 700 !important;
}
.large-button:active, a.large-button:active {
background: -moz-linear-gradient(top,  #555555,  #333333) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#333333)) !important;
text-shadow: 0px 1px 0px #111 !important;
color: #aeaeae !important;
}
.large-button:hover{
color: #FF8B01 !important;
}
.large-button.submit, a.large-button.submit {
background: -moz-linear-gradient(top,  #555555,  #333333) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#333333)) !important;
text-shadow: 0px 1px 0px #111 !important;
color: #aeaeae !important;
}
.large-button.submit:hover,	a.large-button.submit:hover {
border-color:#2b2f39 !important;
background-color: #2b2f39 !important;
color: #FF8B01 !important;
}
.large-button.submit:active, a.large-button.submit:active {
background: -moz-linear-gradient(top,  #555555,  #333333) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#333333)) !important;
text-shadow: 0px 1px 0px #111 !important;
color: #aeaeae !important;
}
.large-button.disabled,	a.large-button.disabled {
border-color: #ddd !important;
background-color: #fff !important;
color: #888 !important;
}
button, input[type="button"], input[type="reset"], input[type="submit"]{
background: -moz-linear-gradient(top,  #555555,  #333333) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#555555), to(#333333)) !important;
text-shadow: 0px 1px 0px #111 !important;
color: #aeaeae !important;
border-radius: 3px !important;
border-style:solid !important;
border: 0px !important;
border-color: #222222 !important;
padding:10px;
font-weight: 700 !important;
box-shadow: 0px 1px 1px #222;
}
button:hover, input[type="button"]:hover, input[type="reset"]:hover, input[type="submit"]:hover{
color: #FF8B01 !important;
}
.mypost, .mypost .postbit-post, .mypost .postbit-details{
background: #383838 !important;
}
.mypost .postbit-details-usertitle, .mypost .postbit-details-username a{
color: #FF8B01 !important;
}
.mypost blockquote.quote{
border-color: #3c414d !important;     
}
.postbit-details-username{
color: #bdbdbd !important;
}
.postbit-details-username a{
color: #bdbdbd !important;
text-shadow: 0px 1px 0px #111 !important;
font-size:105% !important;
}
.postbit-details-username a:hover{
color: #FF8B01 !important; 
}
.postbit-details-usertitle {
color: #bdbdbd !important;
}
/* Menus */
.dropdown {
background: #333333 !important;
border-color: #444444 !important;
border-style:solid !important;
border-width:1px !important;
clip: rect(auto auto 500px auto) !important;
box-shadow: 0px 2px 2px #111 !important;
}
.dropdown > li:hover > a {
color: #FF8B01 !important;
background: -moz-linear-gradient(top,  #3c414d,  #2f343d) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;
box-shadow: 0px 1px 0px #111 !important;
}
.forum-bit-lastpost{
border-color: #333333 !important;
}
/* Tabs */
.forum-tabs li.activetab a, .forum-tabs a:hover{ 
background-color: #3c414d !important;
color:#FF8B01 !important;
text-shadow: 1px 1px 2px #000000 !important;
font-weight:bold !important;
}
.forum-tabs > li > a:after {
border-color: transparent transparent transparent #282828 !important;
}
.forum-tabs > li.activetab > a:after {
border-color: transparent transparent transparent #3c414d !important;
}
.forum-tabs > li > a {
background-color: #282828 !important;
}
.forum-tabs > li > a:hover:after,.forum-tabs > li.child > a:hover:after{
border-color: transparent transparent transparent #3c414d !important;
}
/* Tabs - bottom */
.forum-tabs.bot{
border-color: #3c414d !important;
}
.forum-tabs.bot > li > a:before {
border-color: #1e1e1e transparent transparent transparent !important;
}
.forum-tabs.bot > li > a:after {
border-color: #282828 transparent transparent transparent !important;
}
.forum-tabs.bot > li.activetab > a:after {
border-color: #3c414d transparent transparent transparent !important;
}
.forum-tabs.bot > li > a:hover:after, .forum-tabs-bot > li.child > a:hover:after{
border-color: #3c414d transparent transparent transparent !important;
}
/* Front Page */
#forum-list{
background: #252628 !important;
}
#forum-extras{
background: -moz-linear-gradient(top,  #3c414d,  #2f343d) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;
} 
.footer-links{
margin-top:0px !important;
border-color:#222222 !important;
}
.footer-links:after {
color:#FF8B01 !important;
content:"NeoGAF Pro Dark style created by sky :)" !important;
text-shadow: 1px 1px 2px #111111 !important;
}
/* Reply */
.fieldset{
border-color: #444444 !important; 
}
/* User CP */
.usercp-sidebar > dl > dd.alt1 > a, .usercp-sidebar > dl > dd.alt1 > span{
 background: -moz-linear-gradient(top,  #3c414d,  #2f343d) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;  
color:#FF8B01 !important;
text-shadow: 1px 1px 2px #111111 !important;
font-weight:700 !important;
}
}
