#new_post_label_chat:active .new_post_label_icon{background-position:-298px -5px !important}
#new_post_label_audio:active .new_post_label_icon{background-position:-373px -5px !important}
#new_post_label_video:active .new_post_label_icon{background-position:-448px -5px !important}

