css:
@-moz-document domain('ptchan.net'), url-prefix('http://ptchan.net') {html, body {
background:#3b3b3b;
color:#FFFFFF;
}
img{
-moz-border-top-left-radius: 0px;
-moz-border-top-right-radius: 15px;
-moz-border-bottom-left-radius: 15px;
-moz-border-bottom-right-radius: 15px;
border-top-left-radius: 0px;
border-top-right-radius: 15px;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
}
* {
font-family: "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif;
font-size: 10pt;
}
input, textarea {
background-color: #3b3b3b;
color: #ffffff;
border: 1px solid #202020;
border-top-left-radius: 0px;
border-top-right-radius: 15px;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
-moz-border-top-left-radius: 0px;
-moz-border-top-right-radius: 15px;
-moz-border-bottom-left-radius: 15px;
-moz-border-bottom-right-radius: 15px;
}
a {
background:inherit;
color: gray;
text-decoration: none;
cursor: pointer;
}
a:visited {
background:#000000;
color: gray;
}
a:hover {
color: #FFF;
background:#000000;
text-decoration:underline;
}
a.quotelink {
background:inherit;
color:#ffffff;
}
.logo {
clear:both;
text-align:center;
background:inherit;
font-size:24pt;
color: #ffcb01;
width:100%;
font-weight: bold;
font-family:"Sans-serif", Verdana, serif;
}
.postarea {
background:inherit;
}
.postblock {
background: #202020;
border: 1px solid #303030;
color: #ffffff;
font-weight: bold;
padding: 2px 5px 2px 5px;
border-top-left-radius: 0px;
border-top-right-radius: 15px;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
-moz-border-top-left-radius: 0px;
-moz-border-top-right-radius: 15px;
-moz-border-bottom-left-radius: 15px;
-moz-border-bottom-right-radius: 15px;
}
.footer {
text-align:center;
font-size:12px;
}
.unkfunc {
background:inherit;
color: #ffcb01;
}
.filesize {
text-decoration:none;
}
.filetitle {
background:inherit;
color:#606060;
font-weight:800;
}
.postername {
background:inherit;
font-size:11pt;
color:#ffcb01;
font-weight:bold;
}
.postertrip {
background:inherit;
color:#808080;
}
.oldpost {
background:inherit;
color:#404040;
font-weight:800;
}
.omittedposts {
background:inherit;
color:#707070;
}
.reply {
background:#484848;
color:#FFFFFF;
border:1px solid #101010;
border-top-left-radius: 0px;
border-top-right-radius: 15px;
border-bottom-left-radius: 15px;
border-bottom-right-radius: 15px;
-moz-border-top-left-radius: 0px;
-moz-border-top-right-radius: 15px;
-moz-border-bottom-left-radius: 15px;
-moz-border-bottom-right-radius: 15px;
}
.replyhl {
background:#484848;
color:#707070;
}
.doubledash {
vertical-align:top;
clear:both;
float:left;
}
.replytitle {
background:inherit;
font-size:18px;
color:#000000;
font-weight:800;
}
.commentpostername {
background:inherit;
font-size:11pt;
color:#505050;
font-weight: bold;
}
a.quotejs {
color:#909090;
text-decoration: none;
}
a.quotejs:hover {
font-weight:bold;
}
.adminbar {
text-align: left;
}
#watchedthreads {
background-color: #000000 !important;
border: 1px solid #303030 !important;
	font-size:10pt;
	padding:0px;
	line-height:20px;
}

#watchedthreadsdraghandle{
        color: #000000;
	border: 0px;
	padding:0px;
	line-height:20px;
}
.thumb { cursor: pointer; }
 .reflinkpreview{ background-color: #000000 }}
