.gcSidebarJfk .selectedItem .goog-menuitem-content, .gcSidebarMenu .selectedItem .goog-menuitem-content, .phoneNumber:hover, 
a.gc-appbar-label, .gc-message-name-link, .gbts:hover, .gbzt:hover, #gbztms1:hover, .gc-bubble-ih, .gb_s.gb_V.gb_k.gb_R:hover, .gb_S:hover, .gb_W:hover {color: #4d8eff !important}

.gb_S, .gb_W, .gb_Z, .gb_X, .gb_1 {color: #777 !important}

.gc-word-high, .gc-word-med2, .gc-word-med1 {color: skyblue !important}

#gb > *, #gbq1, .goog-splitpane-first-container, .goog-splitpane-second-container
{background: #000 !important}

#gbx1 {border-color: #2a2a2a !important; background: #000 !important}
.gbsbis {border-color: silver !important; background: #000 !important}

.selectedItem {font-size: 16px !important; font-weight:bold !important}

.jfk-button-primary {background: navy !important}
.jfk-button-primary:hover {background: #1413b2 !important; border-color: transparent !important} 


.goog-menuitem-content {color: #6d6d6d !important}
.balanceLabel, .balance {color: #555 !important}
.balance:hover {color: green !important}
.gbps3, .talk-select-country-code {color: #444 !important}


a#logo, #gbq1 > * {
  width: 0 !important;
  height: 53px !important;
  padding: 0px 0px 0px 187px !important; /* 187px */
  margin: 10px 0px -4px 20px !important;    /* -13px -11px / -13px -15px */     /* 0px 0px -10px -10px */
  background-color: #000 !important;
  background: url(http://img.photobucket.com/albums/v682/neyel8r/myGV2_zps62922562.gif) no-repeat !important}
