.gb_k.gb_va {margin-left: -20px !important}

img[src="/voice/resources/1366864992-blue_ghost.jpg"] {
  width: 0px !important;
  height: 32px !important; 
  padding-left: 32px !important;
  margin: 0 !important;
  background: url(http://img.photobucket.com/albums/v682/neyel8r/dark_ghost_zpsd33a3eea.jpg) no-repeat !important}
 

.playerContainer, .goog-splitpane-handle, span.gbqfi.gb_Aa {background-color: transparent !important;}

.gc-appbar-refresh-button, .gc-appbar-show-all, .gc-appbar-show-unread, .goog-button-base-content, .jfk-button-narrow.jfk-button-collapse-left, 
.jfk-button-narrow.jfk-button-collapse-right, .gc-message-sms-send, .gc-message-sms-cancel {background: silver !important}
