.goog-menuseparator {border-color: navy !important}

.goog-inline-block.goog-flat-menu-button {background-color: transparent !important}
.goog-inline-block.goog-flat-menu-button-caption:hover {color: #000 !important}
.goog-inline-block.goog-flat-menu-button-caption {color: #555 !important}
.jfk-button-collapse-left:hover, .jfk-button-collapse-right:hover {color: #000 !important}

#gbg4, #gbg5, .gc-searchbar {background-color: #000 !important} /* , #gc-content */
#gbx1, .gc-appbar, .gbtsa, #gc-gaia-bar {background: #000 !important}
.gc-message {border-color: transparent !important}

.gcSidebarItem:hover, .gbmt:hover {background-color: #6c6c6c !important} /* was #7a7a7a */
.gcSidebarItem:hover {color: blue !important}
.gcSidebarItem:hover, .gbmt:hover {-moz-transition: background .250s ease-in-out; -webkit-transition: background .250s ease-in-out; 
    border: 0 none !important; transition: background .250s ease-in-out} /* .175 .333 */

.gc-inbox-sidebar-header, .gc-inbox-view-header {background-color: #2A2A2A !important}
