{height: 22px !important}
.gc-inbox-sidebar-item, .sectionDivider
{border: 1px transparent !important}


.gc-inbox-sidebar-header
{padding-top: 12px !important; margin-top: 0 !important}
.gc-inbox-sidebar-menu {border-style: none !important; background: transparent !important}
.gc-simple-menu2 .goog-flat-menu-button, .gc-simple-menu .goog-flat-menu-button, .gc-control, .gc-message-sms-show {color: #659ab8 !important}
.goog-flat-menu-button.goog-flat-menu-button-open, .gc-message-more-menu:hover {background: silver !important}
.goog-menuitem-highlight {border: none !important}
.goog-menuitem-highlight {-moz-transition: background .175s ease-in-out; -webkit-transition: background .175s ease-in-out; 
    border: 0 none !important; transition: background .175s ease-in-out}
.goog-menuitem-highlight > div, .goog-flat-menu-button.goog-flat-menu-button-hover, .goog-flat-menu-button.goog-flat-menu-button-open, #gb_51:hover, 
#gb_17:hover, #gb_10:hover, #gb_172:hover, #gb_212:hover, #gb_6:hover, #gb_30:hover, #gb_27:hover, #gb_31:hover, #gb_12:hover, a.gbmt:hover 
{color: #0024d6 !important; font-weight: bold !important}
.goog-menuitem-highlight, .goog-menuitem-hover
{padding-bottom: 6px !important; padding-top: 6px !important}
.gc-inbox-sidebar-menu, .gc-inbox-sidebar-types-header, .gc-inbox-sidebar-types-menu, .gc-sidebar-contact-menu {background: transparent !important}

.goog-menuitem-highlight {background-color: #7a7a7a !important}

