.gc-message-sline 
{border-top: 5px #000 !important; border-radius: 5px 0 0 0 !important; background-color: yellow !important}
.gc-status-text {color: #0d0d0d !important}
.gc-message-read .gc-message-sline {background: #080808 !important}
