.ac-renderer div.active {background-color: silver !important; color:darkblue !important}
.ac-highlighted {color: royalblue !important}
.ac-row {color: #6a6a6a !important}
.gc-sms-counter {color: skyblue !important; font-size: 12pt !important}
.gc-bubble-callout {
  border-color: transparent transparent navy transparent !important;
  border-style: solid !important;
  border-width: 10px 7px 10px 7px !important;
  height:0 !important;
  width:0 !important;
  background: none !important;
  position:relative !important;
  top: -8px !important}

