.gc-bubble-tl
  {background: #1b1b1b !important; height: 5px !important; border-top-left-radius: 5px !important}
.gc-bubble-bl
  {background: #1b1b1b !important; height: 5px !important; border-bottom-left-radius: 5px !important}
.gc-bubble-tr
  {background: #1b1b1b !important; height: 5px !important; width: 5px !important; border-top-right-radius: 5px !important}
.gc-bubble-br
  {background: #1b1b1b !important; height: 5px !important; width: 5px !important; border-bottom-right-radius: 5px !important}

.goog-bubble-close-button 
  {top: 1px !important; left: -15px !important; background-color: #000 !important; border-radius: 5px !important}
.gc-appbar, .gc-inbox-sidebar-menu-border-top, .gc-inbox-sidebar-block {border-color: #2A2A2A !important}



