css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goo.ne.jp"),domain("oshiete.goo.ne.jp")  {

#global-header,.entry-bottom-info,.comment-form,#mod-trackbacks,#mod-comments h3,
#mod-mobile,#mod-access-status,#mod-search,#mod-powered-by,#mod-goo-recommends,#mod-recent-comments,#mod-login
{display:none;}
body,
#title-banner-set {padding-top: 5px!important;}
#title-banner-body{height: 45px!important;}

#globalheader,#headnavi,#rog,.google_ad,#q-and-a .ba,#q-and-a .share-bottom,.footer-unit,#footer,
#gooad_rect,IMG[src="http://cdn.oshiete.goo.ne.jp/images/tsubuoshi/banner_300.jpg?20120328_1"],IMG[src="http://cdn.oshiete.goo.ne.jp/images/banner_liferecipe_300.jpg?20120328_1"],
.new-question,.q-head .share ul,
#q-and-a .q-head h2,#tn-top,ul#logo,#header:nth-child(1)>DIV:nth-child(8),
#btn_searchweb_header,#btn_searchcse_header,.bline,.r-qa,.return-top,#ok_footer hr,.section-lv4,.useful-button,.complain,.helpful li input,
.goobookmark,.gooblog,.goomail,
.q-footer,.answer-button,#oshiete-today,.a_direction{display:none!important;}


#header .searcharea{position:relative;
left:0px;width:100%;}
#header .searcharea form{position:relative;
left:60px;width:900px;}
#header #commonsearch_category_header{position:relative;
margin-top:-29px!important;
left:340px;width:400px;}

#header .o_textArea,#header INPUT[type="submit"]:nth-child(18),#btn_searchoption_header{position:relative;
left:-280px;top:-3px;
z-index:9999;}
#header .searcharea{background-image:none!important;
background-color: #bee5f4!important;
border:none!important;}
.q-article, .a-article{padding-top: 5px!important;
background-image:none!important;
border:none!important;}
h2.q,h2.a,.a-head,.sort,ul.sort{background-image:none!important;}
#ok_path{padding-top: 0px!important;
padding-bottom: 0px!important;}
.used{position:absolute;
top:35px;left:540px;}
#q-and-a .q-meta{padding-bottom: 0px!important;}

#q-and-a .section-lv2 h2,.a-title h3,.q-title h3{font-size: 11px!important;}
.a-head div.sort ul.sort,h2.a,ul.meta,.q-article, .a-article{padding-top: 0px!important;
padding-bottom: 0px!important;}


.section-lv2, .section-lv3, .section-lv4
{font-size: 10.5px!important;}


body,#ok-main{background:#fbfbfb!important;}
}
