css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("46.105.117.212") {

/* général */

* {  border: none !important;
     margin: 0 !important; 
     text-decoration: inherit !important;
     outline: none !important;
     padding: 0 !important;
     font-family: inherit !important;
     font-weight: inherit !important;
     font-size: 1em !important;
     background-color: inherit !important;
     position: static !important;
     line-height: inherit !important;
     color: inherit !important;
     }
     
body {
 font-family: Georgia, serif !important;
 font-family: /*[[typo]]*/ !important;
 font-size: 18px !important;
 padding: 2em 2em 2em 4em !important;
 color: black !important;
 line-height: 1.4em !important;
 background-color: white !important;
 }

body:before {
 content: '' !important;
 display: block !important;
 position: fixed !important;
 top: 0  !important;
 left: 0 !important;
 background-color: #009ef3 !important;
 background-color: /*[[couleur]]*/ !important;
 height: 100% !important;
 width: 2em !important;
 }

/* formulaires */

input, select {
 border: solid 1px gray !important;
}

form tr {
 height: 2em !important;
 }
 
/* liens */
 
a, a:visited {
 border-bottom: dotted 1px gray !important;
 }
 
a:hover, a:focus {
 color: #009ef3 !important;
 color: /*[[couleur]]*/ !important;
 border: none !important;
 }

a:active {
 color: black !important;
 position: relative !important;
 top: 1px !important;
}
 
/* navigation */

.barre {
 font-size: 0.8em !important;
 }
 
.barre img {
 display: none !important;
 }
 
.barre span {
 float: none !important;
 display: inline  !important;
 text-transform: capitalize;
 }
 
/* .barre > span:first-child a:before {
 content:'Déconnexion' !important;
 } */

.barre > span:first-child:after {
 content:' ~ ' !important;
 }
 
/* accueil */
 
body > span {
 float: none !important;
 }
 
body > div:not(.barre) {
 height: auto !important;
 width: 100% !important;
}

body > div:not(.barre) > p, h2 {
 font-size: 1.4em !important;
 color: #009ef3 !important;
 color: /*[[couleur]]*/ !important;
 line-height: 3em !important;
 }
 
body > div:not(.barre) img {
 display: none !important;
}

/* notes */

table[border] {
 border-collapse: collapse !important;
 width: 100% !important;
 margin: 1em 0 !important;
 }

table[border] tr:hover {
 background-color: #009ef3 !important;
 background-color: /*[[couleur]]*/ !important;
 color: white !important;
 }

table[border] td {
 border: solid 1px gray !important;
 padding: 0.4em !important;
 }

table[border] tr:first-child td,
table[border] tr:first-child:hover td {
 background-color: white !important;
 border: none  !important;
 text-align: left !important;
 padding: 0 !important;
 color: black !important;
 font-size: 0.8em !important;
 }

textarea[name=com] {
 color: #009ef3 !important;
 color: /*[[couleur]]*/ !important;
 margin: 1em 0 !important;
 padding: 0.4em !important;
}

/* emploi du temps */

#content {
 height: auto !important;
 padding-bottom: 2em  !important;
 }

#ctDiv + div span {
 float: left !important;
 font-size: 0.8em !important;
 height: 2em !important;
 }

#ctDiv + div span a {
 background-color: #009ef3 !important;
 background-color: /*[[couleur]]*/ !important;
 color: white !important;
 border: none !important;
 cursor: help !important;
 }
 
#ctDiv {
 position: absolute !important;
 top: 6em !important;
 left: 35em !important;
 height: auto !important;
 background-color: transparent !important;
  }

form > span > input {
 display: block;
 margin-bottom: 1em !important;
 }

form > span > a[target] { /* imprimer */
 font-size: 0.8em !important;
 }

/* informations */

#epoch_popup_calendar {
 position: absolute !important;
 }

form tr td:first-child {
 width: 15em !important;
 border-bottom: dotted 1px white !important;
 text-align: left  !important;
 }

form tr:hover td:first-child {
 border-bottom-color: #009ef3 !important;
 border-bottom-color: /*[[couleur]]*/ !important;
 color: #009ef3 !important;
 color: /*[[couleur]]*/ !important;
 }

}
