.centercolumn {width: auto !important;border: none !important;background: #fff !important;}
.centercolumn>table {width: 100%;}
.column {width: 180px !important;border-color: #aaa !important; background-color: #fff !important;border: 0px !important;}
.maintable a[href="http://www.mephist.ru"]>img {display: none;}
.maintable table[height="92"] {background: url(http://savepic.su/731344.png)  left no-repeat !important;display: block;}
td.column>table>tbody>tr>td {border-left: 1px solid #aaa !important;border-right: 1px solid #aaa !important;}
