td.column>table>tbody>tr[height="18"]>td {height: 24px !important;font-weight: bold;padding-left: 10px !important;}
.column td.menu1>a, td.menu1>noindex>a, table[width="100%"]>tbody>tr>td>a, table[width="100%"]>tbody>tr>td>noindex>a,  table[width="100%"]>tbody>tr>td>s>noindex>a, td.column>table>tbody>tr>td>a, td.column>table>tbody>tr>td>noindex>a[href*="http://corum.mephist.ru"], td.column>table>tbody>tr>td>noindex>a[href*="http://wiki.mephist.ru"] {
    background: url("http://www.yourdiscovery.com/resources/igroups/images/homepage/arrow_right.gif") no-repeat scroll 95% 50% transparent;
    border-bottom: 1px solid #E3E3E3;
    clear: both;
    display: block;
    float: left;
    font-size: 110%;
    font-weight: bold;
margin-top: -13px !important;
    padding: 0px 15px 3px 15px !important;
    width: 180px;
color: #599CC9 !important;
}
H1 {
display: block !important;
width: 97%;
background: #eee;
font-size: 19px !important;
