css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("dagbladet.no") {
	body {
		background-color: #000000 !important;
	}
	
	a:link {
		-moz-transition: all 0.2s ease 0s !important;
		color: #555555 !important;
		text-shadow: #333333 0em 0em 0.1em !important;
	}
	
	a:visited {
		-moz-transition: all 0.2s ease 0s !important;
		color: #006511 !important;
		text-shadow: #333333 0em 0em 0.1em !important;
	}
	a:hover {
		-moz-transition: all 0.2s ease 0s !important;
		color: #00FF00 !important;
		text-shadow: #00AA00 0em 0em 0.2em !important;
	}

	#menuwrapper {
		background: none repeat scroll 0 0 #151515 !important;
		border-bottom: 5px solid #005500 !important;
	}

	body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, p, blockquote, th, td {
		background-color: #000000 !important;
		color: #008800 !important;
		text-shadow: #006600 0em 0em 0.2em !important;
	}
	
	.ref img {
		-moz-transition: all 0.2s ease 0s !important;
	}

	ul.vertical-lf li a {
		color: #003300 !important;
	}

	.ref h1.extraTitle {
		-moz-transition: all 0.2s ease 0s !important;
		background-color: #363636 !important;
		border-radius: 5px 5px 0 0 !important;
		color: #449900 !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	
	.ref h2 a:link, .ref h2 a:visited {
		-moz-transition: all 0.2s ease 0s !important;
		color: #007000 !important;
		text-shadow: #006600 0em 0em 0.076em !important;
	}
	
	.ref h2 a:hover, .ref h2 a:focus {
		-moz-transition: all 0.2s ease 0s !important;
		color: #00FF00 !important;
		text-decoration: none !important;
		text-shadow: #00BB00 0em 0em 0.24em !important;
	}

	.nav a {
		background: url("http://i.imgur.com/0lBVv.png") no-repeat scroll 50% -9px transparent !important;
		color: #007700 !important;
		text-shadow: #006600 0 0 0.05em !important;
	}
	
	.nav a:hover {
		background-position: 50% 0 !important;
		color: #00FF00 !important;
		text-shadow: #00AA00 0em 0em 0.24em !important;
	}
	
	.db-search-10 input.searchText {
		background: none repeat scroll 0 0 #000000 !important;
		border: 1px solid #00FF00 !important;
		border-radius: 4px 4px 4px 4px !important;
		box-shadow: 0 1px 1px #00AA00 inset !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	
	.db-button.type3 {
		background: url("http://gfx.dagbladet.no/g4/bg-black-btn.gif") repeat-x scroll left center #000000 !important;
		border-color: #003300 !important;
		color: #00FF00 !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	.db-button.type3:hover {
		border-color: #00AA00 !important;
		color: #00FF00 !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	
	.skattesok hr {
		background: none repeat scroll 0 0 #002200 !important;
		color: #00FF00 !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	.skattesok input.sokfelt {
		background-color: #002200 !important;
	}
	.skattesok .topplistene {
		background: none repeat scroll 0 0 #002000 !important;
		border-radius: 4px 4px 4px 4px !important;
		color: #00FF00 !important;
		text-shadow: #009900 0.1em 0.1em 0.2em !important;
	}
	
	.ref {
		border-top: 1px solid #006600 !important;
	}
	
	element.style {
		margin-bottom: -250px !important;
		margin-top: -11px !important;
	}
	
	.l-color5 .xt1, .l-color5 .xt2, .l-color5 .xt3, .l-color5 .xt4 {
		background: none repeat scroll 0 0 #404040 !important;
	}
	
	.partnerboks {
		border-bottom: 0 solid #000000 !important;
		margin-bottom: 0 !important;
		width: 980px !important;
	}
	
	#solBox {
		background: none no-repeat scroll 0 0 transparent !important;
	}
	
	#tvguide .serie, #tvguide .tvsjanger .serie-t {
		background: none repeat scroll 0 0 #333333 !important;
	}
	
	#tvguide .nyheter, #tvguide .tvsjanger .nyheter-t {
		background: none repeat scroll 0 0 #005500 !important;
	}
	
	.skattesok {
		background: none no-repeat scroll 18px 15px #000000 !important;
	}
}
