css:
@-moz-document domain('hootsuite.com'), url-prefix('http://www.hootsuite.com/dashboard') {
	

/*----------------------*/

::-webkit-scrollbar {
  width: 7px;
}
::-webkit-scrollbar:horizontal {
  height:7px;
}
::-webkit-scrollbar-track {
  margin-top: -10px;
  margin-bottom: -10px;
  background:#f0f0f0;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
}
::-webkit-scrollbar-track:disabled {
  display: none;
}
::-webkit-scrollbar-thumb {
  border-width: 19px 0;
  min-height: 20px;
  background:#aaa;
  opacity: 0.4;
  -webkit-border-radius: 5px;
}

.message .networkAvatar {
  border: 0px !important;
  height: 48px !important;
  width: 48px !important;
-webkit-border-radius: 4px !important;
-moz-border-radius: 4px !important;
}
.message .networkAvatarLink{
  -webkit-border-radius: 4px !important;
  -moz-border-radius: 4px !important;
  overflow: hidden !important;
  height: 48px !important;
  width: 48px !important;
  background: none !important;
}

.message .networkName {
  color: #000 !important;
  xtext-shadow: 0px 1px 2px #aaa !important;
  font-size: 13px !important;
  font-family: "Helvetica Neue" !important;
  xline-height:15px !important;
  margin-bottom: 4px !important;
}
.message{
padding-left: 15px !important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fdfdfd), color-stop(100%,#f6f6f6)) !important;
background: -webkit-linear-gradient(top, #fdfdfd 0%,#f6f6f6 100%) !important;
background: -moz-linear-gradient(top, #fdfdfd 0%,#f6f6f6 100%) !important;
border-bottom: 1px solid #ccc !important;
border-top: 1px solid #fff !important;
}

.xheader{
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#565656), color-stop(100%,#333333)) !important;
background: -webkit-linear-gradient(top, #565656 0%,#333333 100%) !important;
background: -moz-linear-gradient(top, #565656 0%,#333333 100%) !important;
border-top: 1px solid #777 !important;
}
.subTitle{
color: #888 !important;
}

.message .postTime{
  color: #a9a9ac !important;
}

.message p {
  color: #222 !important;
}

/*.message p a{color: #846812 !important;}
.message p a:hover{text-decoration: underline; !important;}*/

.messageOptions{
right: 15px !important;
}

.comment {
overflow: hidden;
color: #333 !important;
text-shadow: 1px 1px 0px #f0f0f0 !important;
background: #eee !important;
border-radius: 8px;
}

.commentList .comment{
padding-left: 57px !important;
}

.comment a{
font-weight: bold !important;
}

.comment a:hover{
color: #000 !important;
}

.stream .stream-scroll .messageOptions {
background-color: transparent !important;
background-image: none !important;
background: transparent !important;
}

.message .messageComments .arrow {
color: #dee0e3 !important;
}

.message .messageComments .comment {
border-bottom:1px solid #bbb !important;
background-color: none !important;
}

.message .postAttachment .description {
color: #444 !important;
}

/*.message a {
color: #846812 !important;
}

.message a:hover {
color: #a17f17 !important;
}*/

.message .postAttachment {
border-color: #DDD !important;
-webkit-box-shadow: inset 0 0px #4C4C4C !important;
-moz-box-shadow: inset 0 0px #4C4C4C !important;
box-shadow: inset 0 0px #4C4C4C !important;
}

.icon-13.preview {
background-position: 0 -27px !important;
}
.message .previousMessages .comment{
border-top: 1px solid #fff !important;
border-bottom: 1px solid #bbb !important;
}

.message .previousMessages .comment p.commentContent {
margin: 0 0 0 55px !important;
}

.message .previousMessages .comment p.commentContent a.networkName{
margin-bottom: 1px !important;
xwidth: 100% !important;
}

.message .messageDescription {
padding: 0 !important;
margin: 0 !important;
}

.message .messageDescription a{
font-size: 11px !important;
}




 
scrollbar {
    -moz-appearance: none !important;
    background: #e0e0e0;
    width: 16px !important;
    min-width: 15px !important;
}
scrollbar[orient="horizontal"] {
    height: 16px;
    min-height: 15px !important;
}


 
scrollbar thumb {
    -moz-appearance: none !important;
    background: rgba(255, 255, 255, 0.5);
    border-style:none;
    border-width:0;
    min-height: 24px;
}
 
scrollbar thumb[orient="horizontal"] {
    min-width: 24px;
}
 
scrollbar thumb:hover {
    background: rgba(255, 255, 255, 0.8);
}
scrollbar thumb:active, scrollbar thumb[active="true"] {
    background: rgba(255, 255, 255, 0.9);
}
 
 

 
scrollbar scrollbarbutton {
    display: none !important;
    visibility: collapse !important
}
 
 

scrollcorner {
    display: none !important;
    
}
}
