/* INFO FEED FIX */

.defaultOfferPanels td {line-height:12px!important; }

.defaultOfferPanels td img {height:13px!important; width: 12px!important; padding: 0px 2px 0px 0px !important; margin: 2px 0px 0px 0px!important;}

.defaultOfferPanels td span {font-size: 11px!important; font-family: 'Tahoma'!important; }

.defaultOfferPanels INPUT {font-size: 11px!important; font-family: 'Verdana'!important; vertical-align: middle!important;}

