css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("video.yandex.ru") {

    body, .b-viewport-zone, .b-likes, .b_video,
    .g-line, 
    .b-media-info .album {
        background-color: white !important;
        }
    .b-media-info .album {
        background-image: none !important;
        }
        
/* Чтобы убрать темную закраску у текущего видео.  */        
    div.b-video_state_current .b-video__i {
        background-color: yellow !important;
        }
    
/* Слайдер похожих видео */
    .l-movie-info .prev, .l-movie-info .next, .l-movie-info .item, .l-movie-info .slider {
        background-color: yellow !important;
        }

/* На странице со списком коллекций черная полоса слева */
    .b-page_theme_black .b-wrapable-table .r1 th, .b-page_theme_black .b-wrapable-table .r1 td, .b-page_theme_black .b-wrapable-table .r2 td
    {
        border-left-color: white !important;
        border-left-color: white !important;
        border-right-color: white !important;
        border-right-color: white !important;
    }
    
    .b-video__title, 
    .title, 
    .b-duration, 
    .text, 
    .action, 
    dd.tags ul li a,
    .b-add-widget__text,
    .b-likes__title,
    .b-pseudo-link,
    .b-share__text,
    .b-menu__name,
    .b-menu__hosting,
    div.b-viewport-zone .b-link,
    .b-viewport__controls__mode-1,
    .totals,
    .b-user a,
    .menu a,
    .b-page-title a,
    .l-movie-info a,
    
    /* Заголовок видео на его странице просмотра */
    .b-page-title h2,
    
    /* Счетчик просмотров на странице просмотра */
    dl.b-media-info dd,
    
    /* Счетчик комментариев на странице просмотра */
    h2.sum,
    
    /* счетчик видео в коллекции */
    .b-collections-sidebar .count,
    
    /* Ссылка на коллекцию на странице просмотра */
    div.b-collections-list ul li a,
    /* .. со страницы своих видео */
    .b-collections-sidebar .title a,
    
    /* Ссылка на перечень коллекций со страницы просмотра видео */
    h3.title a,
    /* .. со страницы своих видео */
    .b-collections-sidebar h2 a,
    
    /* метки */
    .b-tags h2, /* заголовок списка */ 
    .b-tags li.s1 a, 
    
    /* Заголовок коллекции на странице со списком колллекций */
    .b-collection .title a,
    .b-collection .play-all a,
    .b-collection .count
    {
        color: black !important;
    }
    
    .b-pseudo-link:hover,
    .b-share__text:hover,
    .b-video__title:hover,
    .b-menu__hosting:hover,
    div.b-viewport-zone .b-link:hover,
    .b-viewport__controls__mode-1:hover,
    .b-user a:hover,
    .menu a:hover,
    .b-page-title a:hover,
    .l-movie-info a:hover,
    div.b-collections-list ul li a:hover,
    h3.title a:hover,
    .b-collections-sidebar h2 a:hover,
    .b-collections-sidebar .title a:hover,
    .b-tags li.s1 a:hover,
    .b-collection .title a:hover,
    .b-collection .play-all a:hover
    {
        color: red !important;
    }
    
}
