css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain("facebook.com"){



body.login_page,body.fbIndex,body.registration{

background-image:url(http://farm6.static.flickr.com/5176/5477571737_b1b0a980b8_o.jpg)!important;

background-repeat:no-repeat!important;

width:100%!important; 

height:100%!important; 

overflow-y:auto!important; 

overflow-x:hidden!important;

}



.fbIndex .loggedout_menubar_container, 

.login_page .login_page_interstitial, 

.registration .UIContentTopper, 

.registration .UIWindowShade{

min-width:0 !important; 

z-index:-1 !important;  

padding-top:10px!important; 

padding-right:10px!important;

padding-bottom:10px!important;

top:40% !important;

left:50% !important;

background-color:rgba(255,255,255,.2)!important;

}



.fbIndex .loggedout_menubar_container:hover, .login_page .login_page_interstitial:hover{opacity: 1!important;}



.fbIndex #globalContainer #dropmenu_container, 

.fbIndex #globalContainer #content, 

.fbIndex #globalContainer #pageFooter, 

#SetAsHomepage_Callout, 

.login_page #pageFooter, 

.login_page .fb_logo.img, 

.login_page .localeSelectorList, 

.login_page div.rg3fbpz-tooltip .login_page div.fb_content, 

.login_page div.WelcomePage_MainSellCenter, 

.login_page div.WelcomePage_MainSell, 

.login_page #pageFooter, 

.login_page ul.welcome_locales, 

.login_page .signup_box, 

.login_page .linear_language, 

.login_page div#blueBar, 

.login_page div.WelcomePage_Container, 

.signup_bar_container, 

.registration div#blueBar, 

.registration #globalContainer #dropmenu_container, 

.registration #globalContainer #pageFooter{display:none !important}



input[type="text"], input[type="password"], textarea, select { outline: none!important;}



.fbIndex .menu_login_container input[type="text"], 

.fbIndex .menu_login_container input[type="password"], 

.login_page #loginform input[type="text"], 

.login_page #loginform input[type="password"]{ padding:6px !important;  font-size:20px !important;  border:1px solid #424242 !important;  background:#fff!important;  font-family:Georgia!important;  vertical-align:middle!important;  width:60%!important;  background-color:rgba(229,229,229,.8)!important;}



.fbIndex label input, 

.login_page label input{padding:8px !important}



.fbIndex .loggedout_menubar_container .lfloat, 

.fbIndex .loggedout_menubar_container .rfloat, 

.login_page .lfloat{float:none !important}



.fbIndex .menu_login_container .login_form_label_field label, 

.fbIndex .menu_login_container td.html7magic label, 

.fbIndex .menu_login_container .login_form_label_field a, 

.login_page h2.uiHeaderTitle, 

.login_page .login_form_label, 

.login_page #loginform a, 

.login_page .uiInputLabel label, 

.login_page #persistent_login_text{color:black!important; font-size:11px; text-shadow:1px 1px 3px white!important; font-weight:bold!important}





#loginform .fsm.fwn.fcg{text-shadow: 1px 1px 3px black!important;color: white!important;}





.fbIndex .menu_login_container label{color:#424242 !important; margin:2px !important}



.fbIndex .menu_login_container input[type="text"], 

.fbIndex .menu_login_container input[type="password"]{width:90%!important}



.fbIndex .loggedout_menubar_container{ position:fixed !important;  width:450px !important;  height:82px !important;  margin-top:-10px !important;  margin-left:-210px !important;  z-index:-1 !important;  opacity:0.8!important}



.fbIndex .loggedout_menubar{width:auto !important}

.fbIndex .loggedout_menubar_container .lfloat img, 

.fbIndex .loggedout_menubar_container .rfloat #login_form table{display:block !important; margin:0 auto !important}

.fbIndex .loggedout_menubar_container .lfloat img{display:block; margin:-70px auto 20px !important}





.login_page .loggedout_menubar_container{background:none!important; border:none!important}

.login_page .loggedout_menubar{margin-left:auto !important; margin-right:auto !important; width:400px !important; padding-top:0px !important}

.login_page #loginform a{font-weight:bold!important; color:#000!important}

.login_page .login_page_interstitial{padding:10px!important; margin-top:180px!important; border:none!important; opacity:0.8!important}

.login_page .login_page_interstitial:hover{opacity:1!important}





.registration .UIContentTopper{border:none!important}



.registration .UIContentTopper, 

.registration .UIWindowShade{ border:none!important}

.registration #globalContainer #content{margin-top:120px!important}



.registration .UIContentTopper, 

.registration #reg_box label, 

#reg_box #reg div.large_form td.label{ text-shadow:1px 1px 3px white!important;  color:black!important;  vertical-align:middle!important}



.registration #reg_box #reg div.large_form div.field_container select{opacity:0.7!important}



#reg_box #reg div.large_form #birthday_warning, 

#reg_box #reg div.large_form #birthday_warning a{text-shadow:1px 1px 3px black!important; color:white!important}



.registration .field_container input{ font-size:20px !important;  font-family:Georgia!important;  background-color:rgba(229,229,229,.8)!important}

 

#reg_box #reg div.large_form div.reg_btn{opacity:0.8!important}



}
