css:
@-moz-document domain('paleohacks.com') {

body, #main-bar, #header, #content, #footer {background:#222;}

a, a:visited {color:#fff;}

#hlogo {background:url(http://i.imgur.com/Vu7wn.gif) no-repeat;}
#hlogo a {height:61px;width:250px;display:block;}
#hlogo a img {visibility:hidden;}

.module, .owner, #header #topbar, .question-status, #user-about-me, 
.answer-votes, .date_brick, .owner-answer .post-signature {color:#999;}

.comment-hover, .comment-hover .comment-date, ul.undo {background:#333;}

.question-hyperlink,  #subheader h2,
.mini-counts, .votes, .status, .views,
.post-text, .vote-count-post, .module p,
#footer, .module p.label-value, .module p.label-key,
.content-page, .container {color:#999;}

.comment-score span, .comments-link, .comment-user {color:#666;}

.stats-div {background:#333;}

.page-description, .form-item label, .user-details td, 
.reputation-score, .item-multiplier, .status strong, .excerpt {color:#999;}

.tabs-question-user a, .tabs-answer-user a, 
.tabs-favorite-user a, .page-numbers, .recent-activity a {
border:1px solid #999;color:#999;
}

.vote-notification {background:#222;color:#999;}

.summarycount {color:#fff;}

h1, h2, h3, h4, h5, #question-header a {#fff;}

.comment-text, .comments-link, .text-counter {color:#fff;}

.question-hyperlink:visited {color:#666;}

#subheader {
border-bottom: 1px solid #999;
}

.user-link, h1, h2, h3, h4, h5, #question-header a {color:#fff;}

blockquote {
background-color: #333;
}

.module, .owner, #header #topbar, .question-status, #user-about-me, .answer-votes, .date_brick, .owner-answer .post-signature {
color: #080808;
background-color: #333;
}

#tabs a.youarehere, #tabs a:hover {
background: #999;
border: 1px solid #BBB;
border-bottom-color: #999;
color: #080808;
}

.post-tag, .post-tag:hover {
background: #666;
color: #fff;
border-bottom: 1px solid #999;
border-right: 1px solid #999;
}

.post-tag:hover {
background:#000;
}

.question-summary {
border-bottom: 1px dotted #999;
}

.user-link, .comment-user, .comment-user a,
#question-header a {color:#fff}

.relative-time, .badgecount,
.started .reputation-score, .reputation-score,
.user-action-time, .post-menu a,
.comment-date span {color:#d1d1d1;}

.comment-text a, .post-text a {color:#f3950f;}


#wmd-preview, #wmd-input, 
textarea, input[type="text"], select {background:#333;border:1px dotted #666;color:#999;}
#wmd-preview p {margin-bottom:0px;color:#999;}
.grippie {background:#222;}

}
