div#mb_0 { font-size: 16px !important;  font-family: Georgia, Tahoma !important;
color: #333 !important;  background-color: #fff!important; }

/*Colors*/
.chc, .ci, .tbo, HTML, BODY, TABLE, INPUT, TEXTAREA, SELECT { color: 
#333 !important; font-family:   Georgia, Verdana, Lucida Grande, Helvetica, Arial, sans-serif !important; line-height:160% !important; background-color: #fff!important;  }


/*Hide Spam Counter*/
#ds_spam b { visibility: hidden;  }
#ds_spam b::before {  content: "Spam"; visibility: visible; font-weight: 
normal; }


*{font-family: Georgia, tahoma, helvetica, sans-serif; color: #333;}
table.tlc tr.rr:hover,
table.tlc tr.y:hover { background-color: #efefef !important;}
table.tlc tr.ur:hover { background-color: #efefef !important;}


/* message  list */
.thm { background-color: #000 !important ; }
.z, .ur.sr, .rr.sr { background-color: #fff !important ; }
.y, .ur { background-color: #FFEC01 !important ; }
.rr { background-color: #fff!important ; }
TD  { border-color: #fff !important ; border-width: 0.5px}


.sd, .ss, .so, .sb, .sq {
background-color: #fff !important;
}
/* regular links */
span.lk,
a.lc,
a.lk
{
text-decoration: none !important;
font-family:  Verdana, Lucida Grande, Helvetica, Arial, sans-serif !important; 
color: #333 !important;
font-size: 10px !important;
line-height: 120% !important; 
}


/* side border */
div#co div {
border: 0 !important;
}


table.tlc tr.ur td,
table.tlc tr.rr td{
border-bottom: 1px dashed #dedede !important;}

/* labels */
div#nb_0 {
background: none !important;
border: 0;
}

div#nb_0 div {
background: #fff !important;
padding: 0 !important;
border: 0;
}

div#nb_0 div div {
border: dashed 1px #ccc;
background: #fff !important;
}

div#nb_0 div div div {
border: 0 !important;
background: none !important;
}

div#nb_0 div.s,
div#nb_0 div.h{
background: none !important;
border-bottom: dashed 1px #ccc !important;
}

div#nb_0 table,
div#nb_0 table td.tl,
div#nb_0 table td.tr,
div#nb_0 table td.bl,
div#nb_0 table td.br {
background: none !important;
}

div#nb_0 table.nb {
background: #fff !important;
}

div#nb_0 table.nb div.lk {
text-decoration: none !important;
font-family:  Verdana, Lucida Grande, Helvetica, Arial, sans-serif !important; 
color: #333 !important;
font-size: 10px !important;
line-height: 120% !important; 
}

/* hide chat */
#nb_3 {display:none !important;}
#nb_2 {display:none !important;}

