.ccc {display:none !important;}
.msg {background:#fff !important;}
.l {color:#333 !important;}
.l {font-size: 11px !important;}
.l  {font-family:  Verdana, Lucida Grande, Helvetica, Arial, sans-serif !important; }
.lk {color:#333 !important;}
.lk {font-family:  Verdana, Lucida Grande, Helvetica, Arial, sans-serif !important; }
.lk {text-decoration: none !important;}
.lk {font-size: 11px !important;}
.lk {line-height: 130% !important;}
