css:
.shortcuts-area { background: none !important; border: none !important; }
a.user_name { color: #eee !important; }
.il-widget-header-blue, .il-widget-header-blue:link, .il-widget-header-blue:active, .il-widget-header-blue:focus, .il-widget-header-blue:hover, .il-widget-header-blue:visited 
{ background: none !important; }
h1.itsl-header-without-icon > span:first-child, h1.itsl-header-with-icon > span:first-child { background: rgba(50, 50, 50, 0.70) !important;}
.tablelisting td, .tablelisting th, .tablelisting tr { border: none !important; }
.il-widget-header { border-radius: 2px 2px 0 0; }

.il-widget { border-radius: 0 !important; box-shadow: 1px 1px 3px rgba(50,50,50,0.4) !important; }
.il-widget-content-ul h2 { font-size: 1.5em !important; margin: 0.2em 0 !important; }

.itsl-searchbox { border-radius: 6px !important; margin-top: 6px !important; margin-left: -1px !important;}

#ctl00_ContentPlaceHolder_DashboardLayout_ctl09_ctl04_CT .minilisting th { display:none !important;  }
#ctl00_ContentPlaceHolder_DashboardLayout_ctl09_ctl04_CT .minilisting td { padding: 8px 3px !important; font-size: 1.1em !important;  }
a[href='/CommunityRedir.aspx'] { display: none !important; }
#ctl00_ContentPlaceHolder_DashboardLayout .il-widget-bottom { padding-top: 0 !important; margin-top: -10px !important;  }

#BottomNavigationBar_Shortcuts_ShortcutsList { margin-top: 3px !important;}

.personal-menu #menu_settings a, .personal-menu #menu_messages a, .personal-menu li:last-child a { text-transform: lowercase !important; }
.personal-menu #menu_settings a:first-letter, .personal-menu #menu_messages a:first-letter , .personal-menu li:last-child a:first-letter { text-transform: uppercase !important; }

.page-master { background: rgb(236, 236, 236) !important; }
.ccl-pageheader { margin-left: -9px !important; }

.actionlink { padding-left: 90px !important; }
