css:
	body {
		margin: 0;
		padding: 0;
		line-height: 130%;
		text-align: center;
		color: black;
		background-color: #222;
		font-size: 13px; font-family: Helvetica, Arial;
	}
	.container {
		margin: 1em auto;
		margin-top: 50px;
		padding: 14px;
		width: 640px;
		text-align: left;
		border: solid 1px #a8abad;
		-webkit-border-radius: 15px;
		background-color: #822;
	}
	.title {
		margin-top: 40px;
		color: #000;
		text-shadow: 0px 1px 3px #15c;
		text-align: center;
                border: solid 1px #a8abad;
                -webkit-border-radius: 15px;
                background-color: #048;
                padding: 20px;
                width: 500px;
                margin: 1em auto;
                line-height: 100%;
                
	}
	.container {
                -webkit-transition: all 0.4s ease-in-out;
                -moz-transition: all 0.5s ease-in-out;
                 -o-transition: all 0.5s ease-in-out;
                 transition: all 0.5s ease-in-out;
        }
        .container:hover, a:hover {
                background-color: #357;
                color: #5aa;
        }
        a {
                color: #000;
        
        }
