css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxquestions.org") {
.inlineimg {
    display: none !important;
}
.KonaBody #threadslist td img {
    width: 16px;
}

.KonaBody #threadslist td:nth-child(1) {
    padding-right: 0;
}

.KonaBody #threadslist tr td {
    background: transparent !important;
    display: block !important;
    float: left !important;
    width: auto !important;
}

.page > table + table + br + table, .page > table + table + br {
    display: none;
}

table {
    border: medium none !important;
}

.KonaBody #threadslist {
    background: #CCDDCC !important;
}

.KonaBody #threadslist tr:nth-child(2n) {
    background: #C4DDC4 !important;
}

.KonaBody #threadslist .alt1 .smallfont {
    display: none;
}

.KonaBody #threadslist br {
    display: none !important;
}

.KonaBody #threadslist a {
    color: #003366 !important;
}

.page {
    background: transparent !important;
}

body {
    background: #889988 !important;
    font-family: Verdana, Arial, sans-serif !important;
}

.smallfont {
    font-family: Tahoma, Berdana, Arial, sans-serif;
}

.KonaBody .tcat {
    background: #668866 !important;
}

.vbmenu_control {
    background: #CC7700 !important;
}

.KonaBody #threadslist .alt2 .smallfont {
    color: #FF3322;
    text-align: left !important;
}

.KonaBody #threadslist .alt2 .smallfont a {
    background: #CCF4CC;
    left: -2.5ex;
    position: relative;
    text-decoration: none;
}

.KonaBody #threadslist td + td + td + td + td {
    clear: none !important;
    float: right !important;
    margin-left: 0 !important;
    margin-top: -1em !important;
    width: 6.5ex !important;
}

.KonaBody #threadslist .thead, #threadslist .thead * {
    display: inline !important;
    float: none !important;
    margin: 0;
    width: auto !important;
}

.KonaBody #threadslist td + td + td + td {
    clear: left;
    margin-left: 2em !important;
}

input + table td:nth-child(1) img {
    display: none;
}

input + table td:nth-child(1) a:after {
    background: none repeat scroll 0 0 #FFFFFF;
    color: #4444FF;
    content: ">> Make new thread";
    display: block;
    font-size: 130%;
    font-weight: bold;
    margin-bottom: 0.3em;
    padding: 0.2em;
    text-align: center;
}

.forumtools_menu .thead {
    background: none repeat scroll 0 0 #5588FF !important;
}

.KonaBody #threadslist .thead {
    background: transparent;
}

.KonaBody #threadslist .thead + .thead + .thead + .thead {
  float: right !important;
  margin-left: 0 !important;
  margin-top: 0 !important;
}

/* posts view */

#posts .alt1, #posts .alt1Active, #posts .alt2, #posts .alt2Active {
    background: #E6E6E6 !important;
    line-height: 1.6 !important;
    padding: 0.4em 0.8em 0 !important;
    font-size: 14px !important;
}

#posts .alt2, #posts .alt2Active {
   background: #efefef !important;
} 

#posts .thead {
  background: none #cce4cc !important;
  color: black !important;
}

#posts .thead a {
  color: #000 !important;
}

#posts .alt1 .bbcodeblock {
    background-color: #DDDDDD !important;
}

.bigusername {
  font-weight: bold;
}

}
