css:
/* Nu.nl */
/* v1.4 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nu.nl"),
domain("nubijlage.nl"),
domain("nuentoen.nl"),
domain("nufoto.nl"),
domain("nujij.nl"),
domain("nulive.nl"),
domain("nusport.nl"),
domain("nu.tv"),
domain("nuwerk.nl"),
domain("nuzakelijk.nl"),
domain("zie.nl"),
url-prefix(http://www.nu.nl) {
#pushdown,
.adblock_h,
#adblock_v,
.adblock_noborder,
center.articlebodyad,
div.freeblock > iframe,
#middlecolumn > .widgetsection.spacious.clearfix.first,
IFRAME[src^="http://partnerwebsites.mistermedia.nl/"],
.wtf-advertorial,
#sanoma-consent-bar {
  display: none !important
}

body {
margin-top: 0 !important
}

#pageheader {
  margin-top: -6px !important;
  min-height: 30px !important;
  height: 30px !important;
  background-position: left bottom !important;
}

#pageheader .container {
  top: 5px !important;
  left: 0 !important;
  margin: 0 auto !important;
  width: 960px !important
}

#pageheader .container #extensionnav {
  position: relative !important;
  top: 0 !important;
  float: none !important;
  width: 880px !important
}
#middlecolumn {
  width: 76% !important
}

.section ul.archive li {
  margin: 0 17px 0 5px !important
}

#pagewrapper, #footer {
  width: 960px !important
}

#footer .content, #contentwrapper {
  width: 820px !important
}

.caption,
.section ul.archive {
  width: auto !important
}

}
